import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css";
import Navbar from "./Navbar";

const SummaryCard = ({ plan, onClick }) => {
  const { fromCity, destination, startDate, endDate, schedule, cover } = plan;
  const totalItems = schedule.reduce((sum, day) => sum + (day.items?.length || 0), 0);
  const totalDays = schedule.length;

  return (
    <div className="summary-card" onClick={onClick}>
      <div className="summary-left">
        <div className="summary-route">{fromCity} ‚Üí {destination}</div>
        <div className="summary-dates">{startDate} ~ {endDate}</div>
        <div className="summary-count">{totalDays} Â§© ÔΩú ÂÖ± {totalItems} ‰∏™Âú∞ÁÇπ</div>
      </div>
      <div className="summary-right">
        <img src={cover || "/images/default-cover.jpg"} alt="cover" className="summary-cover" />
      </div>
    </div>
  );
};

export default function TravelSummaries() {
  const [plans, setPlans] = useState([]);
  const navigate = useNavigate();

useEffect(() => {
  const fetchPlans = async () => {
    try {
      const userId = localStorage.getItem("userId"); // Á°Æ‰øùÁôªÂΩïÂêéÂ∑≤‰øùÂ≠ò userId
      const response = await fetch(`http://localhost:3001/api/trip/user/${userId}`);
      const data = await response.json();
      setPlans(data);
    } catch (err) {
      console.error("‚ùå Failed to fetch user plans:", err);
    }
  };

  fetchPlans();
}, []);


  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">üåê My Travel Summaries</h2>
        {plans.length === 0 ? (
          <p style={{ padding: "1rem" }}>No saved plans found.</p>
        ) : (
          plans.map((plan, index) => (
            <SummaryCard
              key={index}
              plan={plan}
              onClick={() => navigate("/plan-summary", { state: plan })}
            />
          ))
        )}
      </div>
    </>
  );
}
