import React, { useEffect, useState } from "react";
import "./MyItinerary.css";

function MyItinerary() {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem("myPlan");
    try {
      const parsed = JSON.parse(stored);
      console.log("üì¶ ËØªÂèñÁöÑËÆ°ÂàíÔºö", parsed);
      if (parsed?.schedule?.length > 0) {
        setSchedule(parsed.schedule);
      }
    } catch (err) {
      console.error("‚ùå Ëß£Êûê localStorage Âá∫Èîô:", err);
    }
  }, []);

  const handleChange = (dayIndex, itemIndex, field, value) => {
    const updated = [...schedule];
    updated[dayIndex].items[itemIndex][field] = value;
    setSchedule(updated);
  };

  const handleSave = () => {
    localStorage.setItem("myPlan", JSON.stringify({ schedule }));
    alert("‚úÖ Plan saved successfully!");
  };

  return (
    <div className="my-itinerary-wrapper">
      {schedule.length === 0 ? (
        <div style={{ textAlign: "center", marginTop: "3rem" }}>
          <p style={{ fontSize: "1.2rem" }}>üö´ No itinerary found.</p>
        </div>
      ) : (
        schedule.map((day, i) => (
          <div key={i} className="day-block">
            <h3 className="day-title">{day.date}</h3>
            <div className="item-list">
              {day.items.map((item, j) => (
                <div className="schedule-item-card" key={j}>
                  <div className="item-time">{item.time}</div>
                  <div className="item-details">
                    <input
                      className="item-title"
                      value={item.title}
                      onChange={(e) =>
                        handleChange(i, j, "title", e.target.value)
                      }
                    />
                    <textarea
                      className="item-desc"
                      value={item.description}
                      onChange={(e) =>
                        handleChange(i, j, "description", e.target.value)
                      }
                    />
                    {item.duration && (
                      <div className="item-duration">‚è± {item.duration}</div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        ))
      )}

      <div style={{ textAlign: "center", marginTop: "2rem" }}>
        <button className="apply-btn" onClick={handleSave}>
          Save Plan
        </button>
      </div>
    </div>
  );
}

export default MyItinerary;
