import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [city, setCity] = useState('');
  const [days, setDays] = useState(1);
  const [preferences, setPreferences] = useState('');
  const [plan, setPlan] = useState('');
  const [loading, setLoading] = useState(false);

  const handleSubmit = async () => {
    setLoading(true);
    try {
      const response = await axios.post('http://localhost:5000/api/plan', {
        city,
        days,
        preferences
      });
      setPlan(response.data.plan);
    } catch (err) {
      setPlan('‚ùå Failed to fetch plan.');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-r from-blue-100 to-purple-200 p-8">
      <div className="max-w-2xl mx-auto bg-white shadow-lg rounded-xl p-6 space-y-4">
        <h1 className="text-3xl font-bold text-center text-gray-800 flex items-center justify-center">
          üåç AI Travel Planner
        </h1>

        <div className="flex flex-col md:flex-row gap-4">
          <input
            className="flex-1 p-2 border rounded"
            placeholder="City (e.g. Paris)"
            value={city}
            onChange={(e) => setCity(e.target.value)}
          />
          <input
            className="w-24 p-2 border rounded"
            type="number"
            min="1"
            value={days}
            onChange={(e) => setDays(e.target.value)}
          />
          <input
            className="flex-1 p-2 border rounded"
            placeholder="Preferences (e.g. food, history)"
            value={preferences}
            onChange={(e) => setPreferences(e.target.value)}
          />
          <button
            onClick={handleSubmit}
            className="bg-indigo-600 hover:bg-indigo-700 text-white px-4 py-2 rounded"
            disabled={loading}
          >
            {loading ? 'Loading...' : 'Generate'}
          </button>
        </div>

        {plan && (
          <div className="mt-4 bg-gray-100 p-4 rounded overflow-auto whitespace-pre-wrap">
            {plan}
          </div>
        )}
      </div>
    </div>
  );
}

export default App;
