import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";

export default function NotificationList() {
  const { id } = useParams(); // Áî®Êà∑ID
  const [notifications, setNotifications] = useState([]);

  useEffect(() => {
    const fetchNotifications = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/notification/user/${id}`);
        const data = await res.json();
        setNotifications(data);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñÈÄöÁü•Â§±Ë¥•:", err);
      }
    };

    fetchNotifications();
  }, [id]);

  return (
    <div className="p-4">
      <h2 className="text-xl font-bold mb-4">üîî ÈÄöÁü•ÂàóË°®</h2>
      {notifications.length === 0 ? (
        <p>ÊöÇÊó†ÈÄöÁü•</p>
      ) : (
        <ul className="space-y-3">
          {notifications.map((n) => (
            <li
              key={n.id}
              className={`p-3 rounded-lg shadow ${n.isRead ? "bg-gray-100" : "bg-yellow-100"}`}
            >
              <div className="font-semibold">{n.message}</div>
              <div className="text-sm text-gray-600">{new Date(n.createdAt).toLocaleString()}</div>
              {!n.isRead && (
                <button
                  onClick={async () => {
                    try {
                      await fetch(`http://localhost:3001/api/notification/${n.id}/read`, {
                        method: "PATCH",
                      });
                      setNotifications((prev) =>
                        prev.map((item) =>
                          item.id === n.id ? { ...item, isRead: true } : item
                        )
                      );
                    } catch (err) {
                      console.error("‚ùå Ê†áËÆ∞Â∑≤ËØªÂ§±Ë¥•", err);
                    }
                  }}
                  className="mt-2 text-sm text-blue-500 hover:underline"
                >
                  Ê†áËÆ∞‰∏∫Â∑≤ËØª
                </button>
              )}
            </li>
          ))}
        </ul>
      )}
    </div>
  );
}
