import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./PackingList.css";

export default function PackingList() {
  const { id } = useParams(); // ‰ªé URL ÊãøÁî®Êà∑ ID
  const [packingData, setPackingData] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [newItems, setNewItems] = useState([]);
  const [loading, setLoading] = useState(true);

  // Ëé∑Âèñ packingDataÔºàÊ†πÊçÆÁî®Êà∑ IDÔºâ
  useEffect(() => {
  const fetchPackingData = async () => {
    try {
      const res = await fetch(`http://localhost:3001/api/trip/user/${id}`); // ÊàñÊç¢Êàê userId Êé•Âè£
      let data = await res.json();

      console.log("üì¶ Ëé∑Âèñ packing data:", data);

      // ‚úÖ Â¶ÇÊûú‰∏çÊòØÊï∞ÁªÑÔºåËΩ¨ÊàêÊï∞ÁªÑ
      if (!Array.isArray(data)) {
        console.warn("‚ö†Ô∏è ËøîÂõûÂÄº‰∏çÊòØÊï∞ÁªÑ:", data);
        data = [data];
      }

      const defaultItemNames = ["Ë°£Êúç", "ÈûãÂ≠ê", "Ê¥óÊº±Áî®ÂìÅ"];

const converted = data.map((trip) => {
  const start = new Date(trip.startDate);
  const end = new Date(trip.endDate);
  const days = Math.round((end - start) / (1000 * 60 * 60 * 24)) + 1;

  // ÂàùÂßãÂåñ items
  let items = [];

  if (Array.isArray(trip.schedule)) {
    items = trip.schedule.flatMap((s) => {
      if (Array.isArray(s.items)) {
        return s.items
          .filter((i) => i && (typeof i === "string" || i.name)) // Âè™‰øùÁïôÊúâ name ÁöÑ
          .map((i) =>
            typeof i === "string"
              ? { name: i, packed: false, assignedTo: "Êú™ÂàÜÈÖç" }
              : {
                  name: i.name,
                  packed: i.packed ?? false,
                  assignedTo: i.assignedTo ?? "Êú™ÂàÜÈÖç",
                }
          );
      }
      return [];
    });
  }

  // ‚ö†Ô∏è Â¶ÇÊûú items ÊòØÁ©∫Êï∞ÁªÑÔºåÊàñËÄÖÊâÄÊúâ item ÈÉΩÊ≤°Êúâ nameÔºåÂ∞±Áî®ÈªòËÆ§Áâ©ÂìÅ
  if (!items.length || !items.some((item) => item.name && item.name.trim())) {
    items = defaultItemNames.map((name) => ({
      name,
      packed: false,
      assignedTo: "Êú™ÂàÜÈÖç",
    }));
  }

  return {
    ...trip,
    title: `${trip.fromCity} ‚ûù ${trip.destination}`,
    date: `${start.getMonth() + 1}/${start.getDate()}/${start.getFullYear()} ~ ${
      end.getMonth() + 1
    }/${end.getDate()}/${end.getFullYear()}`,
    days,
    items,
  };
});




      console.log("‚úÖ ËΩ¨Êç¢ÂêéÁöÑ packingData:", converted);
      setPackingData(converted);

      if (converted.length > 0) {
        setSelectedTripId(converted[0].id);
      }

    } catch (err) {
      console.error("‚ùå Ëé∑Âèñ trip Â§±Ë¥•:", err);
    }
  };

  fetchPackingData();
}, [id]);


  // Ëá™Âä®‰øùÂ≠òÂà∞ localStorageÔºàÂèØÈÄâÔºâ
  useEffect(() => {
    if (packingData.length > 0) {
      localStorage.setItem("packingData", JSON.stringify(packingData));
    }
  }, [packingData]);
console.log("üì¶ packingData:", packingData);

  // ‰øÆÊîπ packed Áä∂ÊÄÅ
  const togglePacked = (tripId, index) => {
    const updated = packingData.map((trip) => {
      if (trip.id === tripId) {
        const items = [...trip.items];
        items[index].packed = !items[index].packed;
        return { ...trip, items };
      }
      return trip;
    });
    setPackingData(updated);
  };

  // Ê∑ªÂä†Êñ∞ item
  const handleAddItem = (tripId) => {
    const content = newItems[tripId];
    if (!content?.trim()) return;

    const updated = packingData.map((trip) => {
      if (trip.id === tripId) {
        const items = [...trip.items, { name: content, packed: false, assignedTo: "Êú™ÂàÜÈÖç" }];
        return { ...trip, items };
      }
      return trip;
    });

    setPackingData(updated);
    setNewItems({ ...newItems, [tripId]: "" });
  };

  // ‰øÆÊîπÁâ©ÂìÅÁöÑÂΩíÂ±û‰∫∫
  const updateAssignedTo = (tripId, index, name) => {
    const updated = packingData.map((trip) => {
      if (trip.id === tripId) {
        const items = [...trip.items];
        items[index].assignedTo = name;
        return { ...trip, items };
      }
      return trip;
    });
    setPackingData(updated);
  };

   const selectedTrip = packingData.find((trip) => trip.id === selectedTripId);

//   if (loading) return <p style={{ padding: "2rem" }}>‚è≥ Loading packing list...</p>;

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="packing-page">
        <div className="trip-select">
          <label htmlFor="tripSelect">Êü•ÁúãË°åÁ®ãÔºö</label>
          <select
            id="tripSelect"
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            <option value="">ËØ∑ÈÄâÊã©Ë°åÁ®ã</option>
            {packingData.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.title}
              </option>
            ))}
          </select>
        </div>

        {selectedTrip && (
          <div className="packing-card">
            <div className="packing-header">
              <h3>{selectedTrip.title}</h3>
              <span className="packing-date">
                {selectedTrip.date} „Éª{selectedTrip.days}Â§©
              </span>
            </div>

            <ul className="packing-items">
              {selectedTrip.items.map((item, idx) => (
                <li key={idx} className="packing-item">
                  <input
                    type="checkbox"
                    checked={item.packed}
                    onChange={() => togglePacked(selectedTrip.id, idx)}
                  />
                  <span style={{ textDecoration: item.packed ? "line-through" : "none" }}>
                    {item.name}
                  </span>
                  <span className="assigned-label">@{item.assignedTo}</span>
                  <select
                    className="assigned-select"
                    value={item.assignedTo}
                    onChange={(e) =>
                      updateAssignedTo(selectedTrip.id, idx, e.target.value)
                    }
                  >
                    {["Êú™ÂàÜÈÖç", "Siyu Jin", "Hantao Zhou"].map((name) => (
                      <option key={name} value={name}>
                        {name}
                      </option>
                    ))}
                  </select>
                </li>
              ))}
            </ul>

            <div className="add-item-section">
              <input
                type="text"
                placeholder="Ê∑ªÂä†Êñ∞Áâ©ÂìÅ..."
                value={newItems[selectedTrip.id] || ""}
                  onChange={(e) =>
    selectedTrip &&
    setNewItems({ ...newItems, [selectedTrip.id]: e.target.value })
                }
              />
              <button onClick={() => handleAddItem(selectedTrip.id)}>Ê∑ªÂä†</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
