// src/components/Features.js
import React from 'react';
import './Features.css';

const features = [
  {
    title: 'Team Planning',
    desc: 'Collaborate with teammates in real-time to add itineraries and assign tasks, making travel preparation more efficient.',
    icon: '👥',
  },
  {
    title: 'AI 行程建议',
    subtitle: 'AI Itinerary Suggestions',
    desc: '输入城市与偏好，获取 AI 生成的个性化旅行计划。',
    icon: '🤖',
  },
  {
    title: '行程可视化管理',
    subtitle: 'Visual Trip Dashboard',
    desc: '所有出行计划一览无余，按时间、地点、参与人清晰展示。',
    icon: '📊',
  },
  {
    title: '地图与足迹记录',
    subtitle: 'Travel Footprint Logging',
    desc: '上传照片、打卡地点，自动记录你走过的每一站。',
    icon: '🗺️',
  },
  {
    title: '用户登录与身份识别',
    subtitle: 'Secure Login & Google Auth',
    desc: '支持邮箱与 Google 登录，安全便捷管理你的专属旅程。',
    icon: '🔐',
  },
  {
    title: '打包与费用分配',
    subtitle: 'Packing & Cost Splitting',
    desc: '旅行打包清单、费用透明分摊，减少纷争，提升体验。',
    icon: '💼',
  },
];

function Features() {
  return (
    <section className="features-section">
      <h2 className="features-heading">Core Function</h2>
      <div className="features-grid">
        {features.map((f, i) => (
          <div className="feature-card" key={i}>
            <div className="feature-icon">{f.icon}</div>
            <h3>{f.title}</h3>
            <p className="subtitle">{f.subtitle}</p>
            <p className="desc">{f.desc}</p>
          </div>
        ))}
      </div>
    </section>
  );
}

export default Features;
