import React, { useState } from "react";
import './CreatePlan.css';
import Navbar from './Navbar';

function CreatePlan() {
  const [planMode, setPlanMode] = useState("ai"); // 默认选择 AI 模式

  const handleSubmit = (e) => {
    e.preventDefault();
    console.log("Selected Plan Mode:", planMode);
    // 根据 planMode 决定跳转或逻辑分支
  };

  return (
    <>
    <Navbar />
      <div className="create-plan-container">
        <div className="text-wrapper">
        <h1>Create Your Trip Plan </h1>

        <form className="create-form">

    <div className="form-row">
        <label>Destination:</label>
        <input type="text" placeholder="e.g., Paris" />
    </div>

    <div className="form-row">
        <label>Start Date:</label>
        <input type="date" />
    </div>

    <div className="form-row">
        <label>End Date:</label>
        <input type="date" />
    </div>

    <div className="form-row">
        <label>Who’s joining?</label>
        <input type="text" placeholder="Enter emails or names" />
    </div>

    <div className="form-row">
        <label>Plan Mode:</label>
        <div className="radio-group">
        <label>
        <input
            type="radio"
            name="planMode"
            value="ai"
            checked={planMode === "ai"}
            onChange={() => setPlanMode("ai")}
        />{" "}
            Use AI to help plan
        </label>
        <label style={{ marginLeft: '1.5rem' }}>
        <input
            type="radio"
            name="planMode"
            value="manual"
            checked={planMode === "manual"}
            onChange={() => setPlanMode("manual")}
        />{" "}
            I’ll plan manually
        </label>
    </div>
    </div>

    <button type="submit">Next</button>
    </form>
      </div>
    </div>
    </>
  );
}

export default CreatePlan;
