import React, { useEffect, useState } from "react";
import { Link, useNavigate } from "react-router-dom";
import "./Navbar.css";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:3001";

function Navbar() {
  const navigate = useNavigate();
  const [userId, setUserId] = useState(() => localStorage.getItem("userId"));
  const [unreadCount, setUnreadCount] = useState(0);

  // 尝试确保拿到 userId（本地→后端）
  const ensureUserId = async () => {
    // 1) 先从本地拿
    let id = localStorage.getItem("userId");
    if (id) {
      setUserId(id);
      return id;
    }

    // 2) 再问后端（你可以把 /api/auth/me 换成 /api/me，取决于你后端挂哪一个）
    try {
      const r = await fetch(`${API_BASE}/api/auth/me`, { credentials: "include" });
      if (r.ok) {
        const d = await r.json();
        if (d?.user?.id) {
          id = String(d.user.id);
          localStorage.setItem("userId", id);
          setUserId(id);
          return id;
        }
      }
    } catch (_) {}
    return null;
  };

  // 拉未读通知
  useEffect(() => {
    let stopped = false;

    const fetchUnread = async () => {
      const id = userId || (await ensureUserId());
      if (!id || id === "null") {
        if (!stopped) setUnreadCount(0);
        return;
      }

      try {
        const res = await fetch(
          `${API_BASE}/api/notification/user/${id}/unread-count`,
          { credentials: "include" }
        );
        if (!res.ok) {
          // 容错：失败就显示 0，不要让页面报错
          if (!stopped) setUnreadCount(0);
          return;
        }
        const data = await res.json();
        if (!stopped) setUnreadCount(Number(data?.count || 0));
      } catch (err) {
        console.error("❌ 获取未读通知数量失败", err);
        if (!stopped) setUnreadCount(0);
      }
    };

    fetchUnread();
    return () => {
      stopped = true;
    };
    // 依赖 userId：拿到后会再请求一次
  }, [userId]);

  // 退出登录
  const handleSignOut = async (e) => {
    e.preventDefault();
    try {
      await fetch(`${API_BASE}/api/auth/logout`, {
        method: "POST",
        credentials: "include",
      });
    } catch {}
    // 清理本地态并跳转登录
    localStorage.removeItem("userId");
    setUserId(null);
    setUnreadCount(0);
    navigate("/login");
  };

  return (
    <div className="navbar">
      <div className="logo">
        <Link to="/home">GoGoGlobe</Link>
      </div>

      <div className="nav-links">
        <Link to="/summary-card">My Plan</Link>

        <Link
          to={userId ? `/notifications/${userId}` : "/login"}
          className="notification-link"
          onClick={(e) => {
            if (!userId) {
              e.preventDefault();
              navigate("/login");
            }
          }}
        >
          <div style={{ position: "relative", display: "inline-block" }}>
            <span role="img" aria-label="bell" style={{ fontSize: "20px" }}>
              🔔
            </span>
            {unreadCount > 0 && (
              <span
                style={{
                  position: "absolute",
                  top: "-5px",
                  right: "-5px",
                  background: "red",
                  color: "white",
                  borderRadius: "50%",
                  padding: "2px 6px",
                  fontSize: "12px",
                  fontWeight: "bold",
                }}
              >
                {unreadCount}
              </span>
            )}
          </div>
          <span style={{ marginLeft: "6px" }}>Notification</span>
        </Link>

        <a href="/login" onClick={handleSignOut}>
          Sign Out
        </a>
      </div>
    </div>
  );
}

export default Navbar;
