import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./TripPhoto.css";

export default function TripPhotoPage() {
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [selectedDayIndex, setSelectedDayIndex] = useState(1);
  const [photos, setPhotos] = useState([]);
  const [previewPhoto, setPreviewPhoto] = useState(null);
  const [likes, setLikes] = useState({});
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");

  const fetchPhotos = async (tripId) => {
    const userId = localStorage.getItem("userId");
    try {
      const res = await fetch(`http://localhost:3001/api/photo/trip/${tripId}?userId=${userId}`);
      const data = await res.json();
      setPhotos(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("‚ùå Ëé∑ÂèñÁÖßÁâáÂ§±Ë¥•", err);
      alert("Ëé∑ÂèñÁÖßÁâáÂ§±Ë¥•");
      setPhotos([]);
    }
  };

  const fetchLikes = async (photoId) => {
    const userId = localStorage.getItem("userId");
    try {
      const res = await fetch(`http://localhost:3001/api/photo/${photoId}/likes?userId=${userId}`);
      const data = await res.json();
      setLikes((prev) => ({ ...prev, [photoId]: data }));
    } catch (err) {
      console.error("‚ùå Ëé∑ÂèñÁÇπËµû‰ø°ÊÅØÂ§±Ë¥•", err);
    }
  };

  const fetchComments = async (photoId) => {
    try {
      const res = await fetch(`http://localhost:3001/api/photo/${photoId}/comments`);
      const data = await res.json();
      setComments(data);
    } catch (err) {
      console.error("‚ùå Ëé∑ÂèñËØÑËÆ∫Â§±Ë¥•", err);
    }
  };

  useEffect(() => {
    const fetchTrips = async () => {
      const userId = localStorage.getItem("userId");
      if (!userId) return;

      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${userId}`);
        const data = await res.json();
        setTrips(data);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñÊóÖË°åÂ§±Ë¥•", err);
        alert("Ëé∑ÂèñÊóÖË°åÂàóË°®Â§±Ë¥•");
      }
    };
    fetchTrips();
  }, []);

  useEffect(() => {
    if (!selectedTrip) return;
    fetchPhotos(selectedTrip.id);
  }, [selectedTrip]);

  const days = selectedTrip
    ? Math.ceil((new Date(selectedTrip.endDate) - new Date(selectedTrip.startDate)) / (1000 * 60 * 60 * 24)) + 1
    : 0;

  const handleLike = async (photoId) => {
    const userId = localStorage.getItem("userId");
    try {
      await fetch(`http://localhost:3001/api/photo/${photoId}/like`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId }),
      });
      fetchLikes(photoId);
    } catch (err) {
      console.error("‚ùå ÁÇπËµûÂ§±Ë¥•", err);
    }
  };

  const handleAddComment = async () => {
    const userId = localStorage.getItem("userId");
    try {
      await fetch(`http://localhost:3001/api/photo/${previewPhoto.id}/comments`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ userId, content: newComment }),
      });
      setNewComment("");
      fetchComments(previewPhoto.id);
    } catch (err) {
      console.error("‚ùå ËØÑËÆ∫Â§±Ë¥•", err);
    }
  };

  const handlePreview = (p) => {
    setPreviewPhoto(p);
    fetchLikes(p.id);
    fetchComments(p.id);
  };

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="trip-photo-page">
        <h2 className="page-title">üì∑ My Travel Footprints</h2>

        <div className="trip-dropdown">
          <label>Choose Trip:</label>
          <select
            onChange={(e) => {
              const trip = trips.find((t) => t.id === parseInt(e.target.value));
              setSelectedTrip(trip || null);
              setSelectedDayIndex(1);
            }}
          >
            <option value="">-- Select a Trip --</option>
            {trips.map((t) => (
              <option key={t.id} value={t.id}>
                {t.fromCity} ‚Üí {t.destination}
              </option>
            ))}
          </select>
        </div>

        {selectedTrip && (
          <div className="day-selector">
            <label>Select Day:</label>
            <select
              value={selectedDayIndex}
              onChange={(e) => setSelectedDayIndex(parseInt(e.target.value))}
            >
              {Array.from({ length: days }, (_, i) => (
                <option key={i} value={i + 1}>
                  Day {i + 1}
                </option>
              ))}
            </select>
          </div>
        )}

        {selectedTrip && (
          <div className="photo-card">
            <div className="photo-card-content">
              <div className="left-column">
                <h3 className="day-title">üìÖ Day {selectedDayIndex}</h3>
                <div className="photo-grid">
                  {photos
                    .filter((p) => p.dayIndex === selectedDayIndex)
                    .map((p) => (
                      <div key={p.id} className="photo-item" onClick={() => handlePreview(p)}>
                        <img src={p.imageUrl} alt="Uploaded" />
                        <p>{p.placeName}</p>
                      </div>
                    ))}
                </div>
              </div>
              <div className="right-column">
                <UploadPhotoForm
                  tripId={selectedTrip.id}
                  dayIndex={selectedDayIndex}
                  onUpload={() => fetchPhotos(selectedTrip.id)}
                />
              </div>
            </div>
          </div>
