import React, { useState, useEffect } from "react";
import "./PackingList.css";
import Navbar from "./Navbar";

const initialPackingData = [
  {
    id: 1,
    title: "San Jose ➝ Sequoia Nation Park",
    date: "6/5/2025 ~ 6/7/2025",
    days: 3,
    items: [
      { name: "登山鞋", packed: false, assignedTo: "未分配" },
      { name: "帽子", packed: false, assignedTo: "未分配" },
      { name: "防晒霜", packed: false, assignedTo: "未分配" },
      { name: "驱蚊液", packed: false, assignedTo: "未分配" },
      { name: "水瓶", packed: false, assignedTo: "未分配" },
      { name: "相机", packed: false, assignedTo: "未分配" },
    ],
  },
  {
    id: 2,
    title: "San Jose ➝ Seattle",
    date: "8/8/2025 ~ 8/15/2025",
    days: 8,
    items: [
      { name: "行李箱", packed: false, assignedTo: "未分配" },
      { name: "衣物 x8", packed: false, assignedTo: "未分配" },
      { name: "洗漱包", packed: false, assignedTo: "未分配" },
      { name: "充电宝", packed: false, assignedTo: "未分配" },
      { name: "雨衣", packed: false, assignedTo: "未分配" },
      { name: "运动鞋", packed: false, assignedTo: "未分配" },
    ],
  },
];

export default function PackingList() {
  const [packingData, setPackingData] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [newItems, setNewItems] = useState({});
    const { id } = useParams();
useEffect(() => {
  async function fetchTrips() {
    try {
      const res = await fetch(`http://localhost:3001/api/trip/${id}`, {
        credentials: "include", // 如果后端用了 cookie/session 登录系统必须加
      });

      const text = await res.text(); // 先拿文本，避免 JSON 报错
      console.log("后端返回内容 text:", text);

      const data = JSON.parse(text); // 尝试转为 JSON
      console.log("✅ 解析后的 JSON:", data);

      const formatted = data.map((trip) => {
        const start = new Date(trip.startDate);
        const end = new Date(trip.endDate);
        const days = Math.round((end - start) / (1000 * 60 * 60 * 24)) + 1;
        return {
          ...trip,
          title: `${trip.fromCity} ➝ ${trip.destination}`,
          date: `${start.getMonth() + 1}/${start.getDate()}/${start.getFullYear()} ~ ${end.getMonth() + 1}/${end.getDate()}/${end.getFullYear()}`,
          days,
          items: trip.schedule?.flatMap((s) => s.items) || [],
        };
      });

      localStorage.setItem("packingData", JSON.stringify(formatted));
    } catch (err) {
      console.error("❌ 获取 trip 失败:", err);
    }
  }

  fetchTrips();
}, []);


useEffect(() => {
  const stored = localStorage.getItem("packingData");
  console.log("raw localStorage:", stored); // ⬅️ 加这一行

  if (stored) {
    const parsed = JSON.parse(stored);
    console.log("parsed packingData:", parsed); // ⬅️ 加这一行

    const converted = parsed.map((trip) => {
      const start = new Date(trip.startDate);
      const end = new Date(trip.endDate);
      const days = Math.round((end - start) / (1000 * 60 * 60 * 24)) + 1;
      return {
        ...trip,
        title: `${trip.fromCity} ➝ ${trip.destination}`,
        date: `${start.getMonth() + 1}/${start.getDate()}/${start.getFullYear()} ~ ${end.getMonth() + 1}/${end.getDate()}/${end.getFullYear()}`,
        days,
        items: trip.schedule?.flatMap((s) => s.items) || [],
      };
    });

    console.log("converted packingData:", converted); // ⬅️ 再加这一行
    setPackingData(converted);
    if (converted.length > 0) {
      setSelectedTripId(converted[0].id);
    }
  } else {
    setPackingData(initialPackingData);
    setSelectedTripId(initialPackingData[0].id);
  }
}, []);


//   useEffect(() => {
//     const stored = localStorage.getItem("packingData");
//     if (stored) {
//       const parsed = JSON.parse(stored);
//       setPackingData(parsed);
//       if (parsed.length > 0) {
//         setSelectedTripId(parsed[0].id); // ✅ 默认选第一个行程
//       }
//     } else {
//       setPackingData(initialPackingData);
//       setSelectedTripId(initialPackingData[0].id); // ✅ 默认选第一个行程
//     }
//   }, []);

  useEffect(() => {
    if (packingData.length > 0) {
      localStorage.setItem("packingData", JSON.stringify(packingData));
    }
  }, [packingData]);

  const togglePacked = (tripId, index) => {
    const updated = packingData.map((trip) => {
      if (trip.id === tripId) {
        const updatedItems = [...trip.items];
        updatedItems[index].packed = !updatedItems[index].packed;
        return { ...trip, items: updatedItems };
      }
      return trip;
    });
    setPackingData(updated);
  };

  const handleAddItem = (tripId) => {
    const content = newItems[tripId];
    if (!content?.trim()) return;
    const updated = packingData.map((trip) => {
      if (trip.id === tripId) {
        const updatedItems = [...trip.items, { name: content, packed: false, assignedTo: "未分配" }];
        return { ...trip, items: updatedItems };
      }
      return trip;
    });
    setPackingData(updated);
    setNewItems((prev) => ({ ...prev, [tripId]: "" }));
  };

  const updateAssignedTo = (tripId, index, newPerson) => {
    const updated = packingData.map((trip) => {
      if (trip.id === tripId) {
        const updatedItems = [...trip.items];
        updatedItems[index].assignedTo = newPerson;
        return { ...trip, items: updatedItems };
      }
      return trip;
    });
    setPackingData(updated);
  };

  const selectedTrip = packingData.find((trip) => trip.id === selectedTripId);
console.log("packingData loaded:", packingData);
  return (
    <>
      <Navbar />
      <div className="packing-page">
        <div className="trip-select">
          <label htmlFor="tripSelect">查看行程：</label>
          <select
  id="tripSelect"
  value={selectedTripId ?? ""}
  onChange={(e) => setSelectedTripId(Number(e.target.value))}
>
  <option value="">请选择行程</option>
  {packingData.map((trip) => (
  <option key={trip.id} value={trip.id}>
    {trip.title}
  </option>
))}

</select>

        </div>

        {selectedTrip && (
          <div className="packing-card">
            <div className="packing-header">
              <h3>{selectedTrip.title}</h3>
              <span className="packing-date">
                {selectedTrip.date} ・{selectedTrip.days}天
              </span>
            </div>

            <ul className="packing-items">
              {selectedTrip.items.map((item, idx) => (
                <li key={idx} className="packing-item">
                  <input
                    type="checkbox"
                    checked={item.packed}
                    onChange={() => togglePacked(selectedTrip.id, idx)}
                  />
                  <span style={{ textDecoration: item.packed ? "line-through" : "none" }}>
                    {item.name}
                  </span>
                  <span className="assigned-label">@{item.assignedTo}</span>
                  <select
                    className="assigned-select"
                    value={item.assignedTo}
                    onChange={(e) => updateAssignedTo(selectedTrip.id, idx, e.target.value)}
                  >
                    {["未分配", "Siyu Jin", "Hantao Zhou"].map((name) => (
                      <option key={name} value={name}>
                        {name}
                      </option>
                    ))}
                  </select>
                </li>
              ))}
            </ul>

            <div className="add-item-section">
              <input
                type="text"
                placeholder="添加新物品..."
                value={newItems[selectedTrip.id] || ""}
                onChange={(e) =>
                  setNewItems({ ...newItems, [selectedTrip.id]: e.target.value })
                }
              />
              <button onClick={() => handleAddItem(selectedTrip.id)}>添加</button>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
