import React, { useState } from "react";

export default function TripPackingCard({ trip, onUpdate }) {
  const [items, setItems] = useState(trip.items || []);
  const [newItem, setNewItem] = useState("");

  const toggleItem = (index) => {
    const updated = [...items];
    updated[index].packed = !updated[index].packed;
    setItems(updated);
    onUpdate(trip.id, updated);
  };

  const addItem = () => {
    if (!newItem.trim()) return;
    const updated = [...items, { name: newItem, packed: false }];
    setItems(updated);
    onUpdate(trip.id, updated);
    setNewItem("");
  };

  const deleteItem = (index) => {
    const updated = items.filter((_, i) => i !== index);
    setItems(updated);
    onUpdate(trip.id, updated);
  };

  return (
    <div className="packing-card">
      <h3>{trip.title}</h3>
      <p>{trip.date} ãƒ»{trip.days}å¤©</p>
      <ul>
        {items.map((item, index) => (
          <li key={index}>
            <input
              type="checkbox"
              checked={item.packed}
              onChange={() => toggleItem(index)}
            />
            <span style={{ textDecoration: item.packed ? "line-through" : "none" }}>
              {item.name}
            </span>
            <button onClick={() => deleteItem(index)}>ðŸ—‘</button>
          </li>
        ))}
      </ul>
      <div>
        <input
          type="text"
          value={newItem}
          onChange={e => setNewItem(e.target.value)}
          placeholder="Add new item..."
        />
        <button onClick={addItem}>Add âž•</button>
      </div>
    </div>
  );
}
