import React, { useState } from "react";

export default function UploadPhotoForm({ tripId, dayIndex, onUpload }) {
  const [files, setFiles] = useState([]);
  const [placeName, setPlaceName] = useState("");
  const [description, setDescription] = useState("");
  const [visibility, setVisibility] = useState("public"); // ÈªòËÆ§ÊòØ public
  const [uploading, setUploading] = useState(false);

  const handleUpload = async () => {
    const userId = localStorage.getItem("userId");
    if (!userId || !tripId || !dayIndex || files.length === 0) {
      alert("ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØÂπ∂ÈÄâÊã©ÁÖßÁâá");
      return;
    }

    setUploading(true);

    try {
      for (const file of files) {
        const formData = new FormData();
        formData.append("photo", file);
        formData.append("tripId", tripId);
        formData.append("uploadedBy", userId);
        formData.append("dayIndex", dayIndex);
        formData.append("placeName", placeName);
        formData.append("description", description);
        formData.append("visibility", visibility); // üëà ÂèØÈÄâ public/private

        const res = await fetch("http://localhost:3001/api/photo", {
          method: "POST",
          body: formData,
        });

        if (!res.ok) throw new Error(await res.text());
      }

      setFiles([]);
      setPlaceName("");
      setDescription("");
      alert("‚úÖ ‰∏ä‰º†ÊàêÂäüÔºÅ");
      onUpload(); // ÈÄöÁü•Áà∂ÁªÑ‰ª∂Âà∑Êñ∞
    } catch (err) {
      console.error("‚ùå ‰∏ä‰º†Â§±Ë¥•:", err);
      alert("‰∏ä‰º†Â§±Ë¥•Ôºö" + err.message);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-form-card">
      <h4>üì§ Upload New Photo</h4>

      <input
        type="file"
        multiple
        accept="image/*"
        onChange={(e) => setFiles(Array.from(e.target.files))}
      />

      <input
        type="text"
        placeholder="üìç Place Name"
        value={placeName}
        onChange={(e) => setPlaceName(e.target.value)}
      />

      <input
        type="text"
        placeholder="üìù Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />

      <select
        value={visibility}
        onChange={(e) => setVisibility(e.target.value)}
      >
        <option value="public">üåç Public</option>
        <option value="private">üîí Private</option>
      </select>

      <button onClick={handleUpload} disabled={!files.length || uploading}>
        {uploading ? "Uploading..." : "Upload"}
      </button>
    </div>
  );
}
