import React, { useState, useMemo } from "react";

const ALLOW_TYPES = ["image/jpeg", "image/png", "image/webp"];
const MAX_MB = 15; // ÂçïÂº†ÊúÄÂ§ß 15MBÔºåÂèØÊåâÈúÄË∞ÉÊï¥

export default function UploadPhotoForm({ tripId, dayIndex, onUpload }) {
  const [files, setFiles] = useState([]);
  const [placeName, setPlaceName] = useState("");
  const [description, setDescription] = useState("");
  const [visibility, setVisibility] = useState("public"); // public | private
  const [uploading, setUploading] = useState(false);

  const userId = useMemo(() => localStorage.getItem("userId"), []);

  const handlePick = (e) => {
    const picked = Array.from(e.target.files || []);
    // ËøáÊª§ + ÊèêÁ§∫
    const bad = [];
    const ok = picked.filter((f) => {
      const validType = ALLOW_TYPES.includes(f.type);
      const validSize = f.size <= MAX_MB * 1024 * 1024;
      if (!validType || !validSize) bad.push(`${f.name}`);
      return validType && validSize;
    });
    if (bad.length) {
      alert(
        `‰ª•‰∏ãÊñá‰ª∂Á±ªÂûãÊàñÂ§ßÂ∞è‰∏çÁ¨¶ÂêàË¶ÅÊ±ÇÔºà‰ªÖ JPG/PNG/WebPÔºå‚â§ ${MAX_MB}MBÔºâÔºö\n` +
          bad.join("\n")
      );
    }
    setFiles(ok);
  };

  // ÂêëÂêéÁ´ØË¶ÅÈ¢ÑÁ≠æÂêç URL
  async function getSignedUrl(file, { userId, tripId, kind }) {
    const r = await fetch("http://localhost:3001/api/upload/sign", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        contentType: file.type,
        userId,
        tripId,
        kind, // 'public' or 'private'
        size: file.size, // ÂèØËÆ©ÂêéÁ´ØÂÅöÂ§ßÂ∞èÊ†°È™å
      }),
    });
    if (!r.ok) throw new Error("Ëé∑ÂèñÁ≠æÂêçÂ§±Ë¥•Ôºö" + (await r.text().catch(() => "")));
    return r.json(); // { uploadUrl, key, publicUrl }
  }

  const handleUpload = async () => {
    if (!userId || !tripId || dayIndex == null) {
      alert("Áº∫Â∞ëÂøÖË¶Å‰ø°ÊÅØÔºàuserId / tripId / dayIndexÔºâ");
      return;
    }
    if (!files.length) {
      alert("ËØ∑ÈÄâÊã©ÁÖßÁâá");
      return;
    }

    setUploading(true);
    try {
      // ‰∏≤Ë°åÊõ¥Á®≥ÔºàÂ•ΩÂÅöÈîôËØØÂ§ÑÁêÜÔºâÔºå‰Ω†‰πüÂèØ‰ª• Promise.all Âπ∂Âèë
      for (const file of files) {
        // 1) ÂèñÁ≠æÂêç
        const { uploadUrl, key, publicUrl } = await getSignedUrl(file, {
          userId,
          tripId,
          kind: visibility === "private" ? "private" : "public",
        });

        // 2) PUT Áõ¥‰º†Âà∞ S3ÔºàContent-Type ÂøÖÈ°ª‰∏ÄËá¥Ôºâ
        const putRes = await fetch(uploadUrl, {
          method: "PUT",
          headers: { "Content-Type": file.type },
          body: file,
        });
        if (!putRes.ok) {
          throw new Error(
            `‰∏ä‰º†Âà∞ S3 Â§±Ë¥•Ôºö${file.name} ‚Äî ` + (await putRes.text().catch(() => ""))
          );
        }

        // 3) ÊääÂÖÉÊï∞ÊçÆÂ≠òÂà∞ÂêéÁ´ØÔºàÊ≥®ÊÑèËøôÈáåÁî® JSONÔºå‰∏çÂÜçÁî® FormDataÔºâ
        const metaRes = await fetch("http://localhost:3001/api/photo", {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            tripId,
            uploadedBy: userId,
            dayIndex: Number(dayIndex),
            placeName: placeName || null,
            description: description || null,
            visibility,              // 'public' | 'private'
            imageUrl: publicUrl,     // S3 ËÆøÈóÆ URLÔºàÁßÅÊúâÊ°∂ÂèØÁïôÁ©∫ÊàñÂ≠ò‰∫∫Á±ª‰∏çÂèØËßÅÁöÑÂç†‰ΩçÔºåÂèñÂÜ≥‰∫é‰Ω†ÁöÑÂÆûÁé∞Ôºâ
            imageKey: key,           // S3 ÂØπË±° keyÔºàÂà†Èô§/Êç¢ CDN ‰ºöÁî®Âà∞Ôºâ
          }),
        });
        if (!metaRes.ok) {
          throw new Error(
            `‰øùÂ≠òÂÖÉÊï∞ÊçÆÂ§±Ë¥•Ôºö${file.name} ‚Äî ` + (await metaRes.text().catch(() => ""))
          );
        }
      }

      // reset
      setFiles([]);
      setPlaceName("");
      setDescription("");
      alert("‚úÖ ÂÖ®ÈÉ®ÁÖßÁâá‰∏ä‰º†ÊàêÂäü");
      onUpload?.(); // ÈÄöÁü•Áà∂ÁªÑ‰ª∂Âà∑Êñ∞
    } catch (err) {
      console.error("‚ùå ‰∏ä‰º†Â§±Ë¥•:", err);
      alert(err.message || "‰∏ä‰º†Â§±Ë¥•");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-form-card">
      <h4>üì§ Upload New Photo</h4>

      <input
        type="file"
        multiple
        accept={ALLOW_TYPES.join(",")}
        onChange={handlePick}
      />

      <input
        type="text"
        placeholder="üìç Place Name"
        value={placeName}
        onChange={(e) => setPlaceName(e.target.value)}
      />

      <input
        type="text"
        placeholder="üìù Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />

      <select
        value={visibility}
        onChange={(e) => setVisibility(e.target.value)}
      >
        <option value="public">üåç Public (ÂÖ¨ÂºÄÊ°∂)</option>
        <option value="private">üîí Private (ÁßÅÊúâÊ°∂)</option>
      </select>

      <button onClick={handleUpload} disabled={!files.length || uploading}>
        {uploading ? "Uploading..." : "Upload"}
      </button>

      {!!files.length && (
        <div style={{ marginTop: 8, fontSize: 12, color: "#6b7280" }}>
          Â∑≤ÈÄâ {files.length} Âº†Ôºõ‰ªÖÊîØÊåÅ JPG/PNG/WebPÔºåÂçïÂº† ‚â§ {MAX_MB}MB
        </div>
      )}
    </div>
  );
}
