import { useEffect, useState } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";

export default function SplitwisePage() {
  const { id } = useParams(); // 当前用户 ID
  const [tripData, setTripData] = useState([]); // 用户所有行程
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [tripList, setTripList] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [members, setMembers] = useState([]);
  const [expenses, setExpenses] = useState([]);

  // 获取所有行程列表
  // 获取用户的所有 trip
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const trips = await res.json();

        if (Array.isArray(trips) && trips.length > 0) {
          setTripData(trips);
          setSelectedTripId(trips[0].id);
        }
      } catch (err) {
        console.error("❌ 获取行程失败:", err);
      }
    };

    fetchTrips();
  }, [id]);

  // 获取当前 trip 的成员
  useEffect(() => {
    if (!selectedTripId) return;

    const fetchMembers = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/members?tripId=${selectedTripId}`);
        const members = await res.json();
        setMemberData(members);
      } catch (err) {
        console.error("❌ 获取成员失败:", err);
      }
    };

    fetchMembers();
  }, [selectedTripId]);

  // 根据选择的行程 ID 加载详情/成员/费用
  useEffect(() => {
    if (!selectedTripId) return;

    const fetchTripDetail = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trips/${selectedTripId}`);
        const trip = await res.json();
        setSelectedTrip(trip);
      } catch (err) {
        console.error("❌ 获取行程详情失败:", err);
      }
    };

    const fetchMembers = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/members?tripId=${selectedTripId}`);
        const data = await res.json();
        setMembers(data);
      } catch (err) {
        console.error("❌ 获取成员失败:", err);
      }
    };

    const fetchExpenses = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/expenses?tripId=${selectedTripId}`);
        const data = await res.json();
        setExpenses(data);
      } catch (err) {
        console.error("❌ 获取费用失败:", err);
      }
    };

    fetchTripDetail();
    fetchMembers();
    fetchExpenses();
  }, [selectedTripId]);

  const handleAddExpense = () => {
    setExpenses([...expenses, { name: "", amount: "", paidBy: "" }]);
  };

  const handleChange = (index, field, value) => {
    const updated = [...expenses];
    updated[index][field] = value;
    setExpenses(updated);
  };

  const handleSave = async () => {
    try {
      const res = await fetch("http://localhost:3001/api/expenses/save", {
        method: "POST",
        headers: {
          "Content-Type": "application/json"
        },
        body: JSON.stringify({ tripId: selectedTripId, expenses })
      });

      if (res.ok) {
        alert("💾 保存成功！");
      } else {
        throw new Error("保存失败");
      }
    } catch (err) {
      console.error("❌ 保存失败:", err);
      alert("❌ 保存失败，请检查控制台");
    }
  };

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="splitwise-page">
        <div className="trip-selector-global">
          <label><strong>查看行程：</strong></label>
          <select
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            <option value="">选择行程</option>
            {tripList.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} → {trip.destination}
              </option>
            ))}
          </select>
        </div>

        {selectedTrip && (
          <div className="splitwise-card">
            <h3>{selectedTrip.fromCity} → {selectedTrip.destination}</h3>
            <p>{selectedTrip.startDate} ~ {selectedTrip.endDate}</p>

            {expenses.map((item, index) => (
              <div className="budget-item" key={index}>
                <input
                  value={item.name}
                  onChange={(e) => handleChange(index, "name", e.target.value)}
                  placeholder="项目名称"
                />
                <input
                  type="number"
                  value={item.amount}
                  onChange={(e) => handleChange(index, "amount", e.target.value)}
                  placeholder="金额"
                />
                <select
                  value={item.paidBy}
                  onChange={(e) => handleChange(index, "paidBy", e.target.value)}
                >
                  <option value="">选择付款人</option>
                  {members.map((member) => (
                    <option key={member.id} value={member.user.email}>
                      {member.user.name}
                    </option>
                  ))}
                </select>
              </div>
            ))}

            <button onClick={handleAddExpense}>➕ 添加费用项</button>
            <button onClick={handleSave} className="save-btn">💾 保存</button>
          </div>
        )}
      </div>
    </>
  );
}
