// Register.js
import React, { useState } from 'react';
import './Register.css';
import { useNavigate, Link } from 'react-router-dom';

export default function Register() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  // 统一使用相对路径；CRA 的 setupProxy.js 会把 /api 代理到 3001
  const API_BASE = '';

  const handleRegister = async (e) => {
    e.preventDefault();
    setMessage('');
    setLoading(true);
    try {
      // 1) 发注册请求（带 credentials 更稳）
      const resp = await fetch(`/api/auth/register`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });

      const data = await resp.json().catch(() => ({}));
      if (!resp.ok) {
        setMessage(`Your Signup Failed：${data?.error || 'unknown error'}`);
        setLoading(false);
        return;
      }

      // 2) 注册成功后，后端已把会话写入；再确认一次并拿到 id
      const me = await fetch(`/api/auth/me`, { credentials: 'include' })
        .then(r => r.ok ? r.json() : { user: null })
        .catch(() => ({ user: null }));

      const id = me?.user?.id ?? data?.userId;
      if (id) {
        localStorage.setItem('userId', String(id));
        setMessage('Registered successfully! Redirecting...');
        // 3) 直接进首页
        setTimeout(() => navigate('/'), 500);
      } else {
        // 极少数情况下会话还没同步好，再让用户去登录页
        setMessage('Registered. Please sign in.');
        setTimeout(() => navigate('/login'), 800);
      }
    } catch (err) {
      console.error(err);
      setMessage('Network error. Please try again.');
    } finally {
      setLoading(false);
    }
  };

  const handleGoogleSignIn = () => {
    // 走同源代理 → CRA dev server 会转发到 3001
    window.location.href = `/api/auth/google`;
  };

  return (
    <div className="register-wrapper">
      <div className="register-card">
        <h1>Create Free Account</h1>
        <p>Plan smart. Travel far. Remember forever.</p>

        <form onSubmit={handleRegister}>
          <input
            type="email"
            placeholder="E-mail Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
            autoComplete="email"
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
            autoComplete="new-password"
          />
          <button type="submit" disabled={loading}>
            {loading ? 'Creating...' : 'Create Free Account'}
          </button>
        </form>

        <div className="message">{message}</div>

        <div className="divider">or</div>
        <div className="google-login" onClick={handleGoogleSignIn}>
          <img
            src="/images/web_light_rd_SU@1x.png"
            alt="Google"
            className="google-icon"
          />
        </div>

        <div className="divider">Already have an account?</div>
        <Link to="/login" className="login-link">Back to Sign in</Link>
      </div>
    </div>
  );
}
