import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import AccommodationForm from "./AccommodationForm"; // ‚úÖ Áî®‰Ω†ÁöÑÁªÑ‰ª∂
import "./Accommodation.css";

export default function AddAccommodationPage() {
  const { id } = useParams(); // Áî®Êà∑ ID
  const [tripList, setTripList] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [accommodations, setAccommodations] = useState([]);

  useEffect(() => {
    const fetchTrips = async () => {
      const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
      const data = await res.json();
      setTripList(data || []);
      if (data.length > 0) setSelectedTripId(data[0].id);
    };
    fetchTrips();
  }, [id]);

  useEffect(() => {
    if (!selectedTripId) return;
    const fetchAccommodations = async () => {
      const res = await fetch(`http://localhost:3001/api/accommodations/trip/${selectedTripId}`);
      const data = await res.json();
      setAccommodations((data || []).sort((a, b) => new Date(a.checkIn) - new Date(b.checkIn)));
    };
    fetchAccommodations();
  }, [selectedTripId]);

  return (
    <>
      <Navbar />
      <SubNavBar />

      <div className="accommodation-container">
        <div className="timeline-column">
          <h3>üïí Â∑≤‰∏ä‰º†‰ΩèÂÆø</h3>
          {accommodations.length === 0 ? (
            <p>ÊöÇÊó†ËÆ∞ÂΩï</p>
          ) : (
            accommodations.map((acc) => (
              <div className="accommodation-card" key={acc.id}>
                <img src={acc.imageUrl} alt="accommodation" />
                <div className="card-info">
                  <h4>{acc.name}</h4>
                  <p>{acc.address}</p>
                  <p>{acc.checkIn} ‚ûù {acc.checkOut}</p>
                  {acc.bookingUrl && <a href={acc.bookingUrl} target="_blank" rel="noreferrer">üîó Êü•ÁúãÈìæÊé•</a>}
                </div>
              </div>
            ))
          )}
        </div>

        <div className="form-column">
          <AccommodationForm
            tripList={tripList}
            selectedTripId={selectedTripId}
            setSelectedTripId={setSelectedTripId}
            onSuccess={() => {
              // ÈáçÊñ∞Âä†ËΩΩ‰ΩèÂÆø‰ø°ÊÅØ
              fetch(`http://localhost:3001/api/accommodations/trip/${selectedTripId}`)
                .then((res) => res.json())
                .then((data) => {
                  setAccommodations((data || []).sort((a, b) => new Date(a.checkIn) - new Date(b.checkIn)));
                });
            }}
          />
        </div>
      </div>
    </>
  );
}
