import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./Splitwise.css"; // 确保你创建了对应样式文件

export default function SplitwisePage() {
  const { id } = useParams();
  const [tripData, setTripData] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [memberData, setMemberData] = useState([]);
  const [expenses, setExpenses] = useState([]);

  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const trips = await res.json();
        if (Array.isArray(trips) && trips.length > 0) {
          setTripData(trips);
          setSelectedTripId(trips[0].id);
        }
      } catch (err) {
        console.error("❌ 获取行程失败:", err);
      }
    };
    fetchTrips();
  }, [id]);

  useEffect(() => {
    if (!selectedTripId) return;

    const fetchMembers = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/members?tripId=${selectedTripId}`);
        const members = await res.json();
        setMemberData(members);
      } catch (err) {
        console.error("❌ 获取成员失败:", err);
      }
    };

    const fetchExpenses = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/expenses?tripId=${selectedTripId}`);
        const data = await res.json();
        setExpenses(data);
      } catch (err) {
        console.error("❌ 获取费用失败:", err);
      }
    };

    fetchMembers();
    fetchExpenses();
  }, [selectedTripId]);

  const saveExpenses = async (updated) => {
    try {
      const res = await fetch("http://localhost:3001/api/expenses/save", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          tripId: selectedTripId,
          expenses: updated.map((e) => ({
            ...e,
            amount: parseFloat(e.amount) || 0,
            payerId: e.payerId ? Number(e.payerId) : null,
          })),
        }),
      });
      if (!res.ok) {
        const text = await res.text();
        console.error("❌ 自动保存失败:", text);
      }
    } catch (err) {
      console.error("❌ 自动保存失败:", err);
    }
  };

  const handleAddExpense = () => {
    const newItem = { note: "", amount: 0, payerId: null };
    const updated = [...expenses, newItem];
    setExpenses(updated);
    saveExpenses(updated);
  };

  const handleChange = (index, field, value) => {
    const updated = [...expenses];
    updated[index][field] = field === "payerId" ? Number(value) : value;
    setExpenses(updated);
    saveExpenses(updated);
  };

  const getUserTotals = () => {
    const totals = {};
    for (const item of expenses) {
      if (!item.payerId || !item.amount) continue;
      totals[item.payerId] = (totals[item.payerId] || 0) + Number(item.amount);
    }
    return totals;
  };

  const totals = getUserTotals();

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="splitwise-page">
        <div className="trip-selector">
          <label htmlFor="tripSelect">选择行程：</label>
          <select
            id="tripSelect"
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            <option value="">选择行程</option>
            {tripData.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} → {trip.destination}
              </option>
            ))}
          </select>
        </div>

        <div className="splitwise-container">
          <div className="splitwise-left">
            <h3>用户总支出</h3>
            <ul>
              {memberData.map((member) => (
                <li key={member.id}>
                  {member.user?.email || "未知用户"}:
                  ¥{totals[member.user.id]?.toFixed(2) || "0.00"}
                </li>
              ))}
            </ul>
          </div>

          <div className="splitwise-card">
            <h3>费用分摊</h3>
            {expenses.map((item, index) => (
              <div className="budget-item" key={index}>
                <input
                  value={item.note}
                  onChange={(e) => handleChange(index, "note", e.target.value)}
                  placeholder="项目名称"
                />
                <input
                  type="number"
                  step="0.01"
                  value={item.amount}
                  onChange={(e) => handleChange(index, "amount", e.target.value)}
                  placeholder="金额"
                />
                <select
                  value={item.payerId ?? ""}
                  onChange={(e) => handleChange(index, "payerId", e.target.value)}
                >
                  <option value="">选择付款人</option>
                  {memberData.map((member) => (
                    <option key={member.id} value={member.user.id}>
                      {member.user?.email || "未知用户"}
                    </option>
                  ))}
                </select>
              </div>
            ))}

            <div className="add-expense-wrapper">
              <button onClick={handleAddExpense} className="add-expense-btn">
                + 添加费用
              </button>
            </div>
          </div>
        </div>
      </div>
    </>
  );
}
