import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css";
import Navbar from "./Navbar";

export default function TravelSummaries() {
  const navigate = useNavigate();
  const [summaries, setSummaries] = useState([]);

  useEffect(() => {
    const planRaw = localStorage.getItem("planData");
    const scheduleRaw = localStorage.getItem("myPlan");

    if (planRaw && scheduleRaw) {
      try {
        const plan = JSON.parse(planRaw);
        const scheduleData = JSON.parse(scheduleRaw);

        const fromCity = plan.fromCity?.trim() || "Ëµ∑ÁÇπÊú™Áü•";
        const destination = plan.destination?.trim() || "ÁõÆÁöÑÂú∞Êú™Áü•";

        const startDate = plan.startDate;
        const endDate = plan.endDate;

        const totalDays = scheduleData.schedule?.length || 0;
        const totalItems = scheduleData.schedule?.reduce((sum, day) => sum + day.items.length, 0);

        setSummaries([
          {
            fromCity,
            destination,
            startDate,
            endDate,
            totalDays,
            totalItems,
            cover: "/images/default-cover.jpg"
          }
        ]);
      } catch (err) {
        console.error("‚ùå Failed to parse planData or myPlan:", err);
      }
    }
  }, []);

  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">üåê My Travel Summaries</h2>

        {summaries.length === 0 ? (
          <p style={{ padding: "1rem" }}>No summaries found.</p>
        ) : (
          summaries.map((summary, index) => (
            <div
              className="summary-card"
              key={index}
              onClick={() => navigate("/plan-summary")}
            >
              <div className="summary-left">
                <div className="summary-route">
                  {summary.fromCity} ‚Üí {summary.destination}
                </div>
                <div className="summary-dates">
                  {summary.startDate} ~ {summary.endDate}
                </div>
                <div className="summary-count">
                  {summary.totalDays} Â§© ÔΩú ÂÖ± {summary.totalItems} ‰∏™Âú∞ÁÇπ
                </div>
              </div>
              <div className="summary-right">
                <img
                  src={summary.cover}
                  alt="cover"
                  className="summary-cover"
                />
              </div>
            </div>
          ))
        )}
      </div>
    </>
  );
}
