import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css";
import Navbar from "./Navbar";

export default function TravelSummaries() {
  const navigate = useNavigate();
  const [card, setCard] = useState(null);

  useEffect(() => {
    const stored = localStorage.getItem("myPlan");
    if (!stored) return;

    try {
      const parsed = JSON.parse(stored);
      const schedule = parsed.schedule || [];

      if (schedule.length === 0) return;

      const startDate = schedule[0].date;
      const endDate = schedule[schedule.length - 1].date;
      const dayCount = schedule.length;

      let city = "Êú™ÊåáÂÆöÂüéÂ∏Ç";
      for (const day of schedule) {
        const item = day.items?.[0];
        if (item?.locationName || item?.title) {
          city = (item.locationName || item.title).split(",")[0];
          break;
        }
      }

      const placeCount = schedule.reduce(
        (acc, day) => acc + (day.items?.length || 0),
        0
      );

      setCard({
        city,
        dateRange: `${startDate} ~ ${endDate}`,
        duration: `${dayCount}Â§©${dayCount - 1}Êôö`,
        placeCount,
        cover: "/images/default-cover.jpg"
      });
    } catch (err) {
      console.error("‚ùå Failed to parse localStorage myPlan:", err);
    }
  }, []);

  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">üåê ÊàëÁöÑÊâÄÊúâË°åÁ®ã</h2>
        {card ? (
          <div className="summary-card" onClick={() => navigate("/plan-summary")}>
            <div className="summary-left">
              <div className="summary-city">
                {card.city} {card.duration}Ê∏∏
              </div>
              <div className="summary-meta">
                <div>{card.dateRange}</div>
                <div>{card.placeCount} ‰∏™Âú∞ÁÇπ</div>
              </div>
            </div>
            <div className="summary-right">
              <img src={card.cover} alt="cover" className="summary-cover" />
            </div>
          </div>
        ) : (
          <p style={{ padding: "1rem" }}>üö´ Ê≤°ÊúâÊâæÂà∞Ë°åÁ®ãÊï∞ÊçÆ</p>
        )}
      </div>
    </>
  );
}
