import React, { useState } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./AddAccommodationForm.css"; // 记得创建 CSS 文件美化

export default function AddAccommodationForm({ tripId, onSuccess }) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!tripId) return alert("❌ 缺少 tripId");
    if (!form.name || !form.address || !form.checkIn || !form.checkOut) {
      return alert("❌ 请填写完整信息");
    }
    if (!imageFile) return alert("❌ 请上传住宿截图");

    const formData = new FormData();
    formData.append("tripId", tripId);
    formData.append("name", form.name);
    formData.append("address", form.address);
    formData.append("checkIn", form.checkIn);
    formData.append("checkOut", form.checkOut);
    formData.append("bookingUrl", form.bookingUrl);
    formData.append("image", imageFile);

    try {
      setLoading(true);
      const res = await fetch("http://localhost:3001/api/accommodations", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();

      if (data.id) {
        alert("✅ 添加成功");
        setForm({ name: "", address: "", checkIn: "", checkOut: "", bookingUrl: "" });
        setImageFile(null);
        document.getElementById("imageInput").value = ""; // 清除 file input
        onSuccess?.();
      } else {
        alert("❌ 添加失败：" + (data.error || "未知错误"));
      }
    } catch (err) {
      console.error("❌ 上传失败", err);
      alert("❌ 网络错误");
    } finally {
      setLoading(false);
    }
  };

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="add-accommodation-form">
        <h3>🏨 添加住宿信息</h3>
        <label>住宿名称：</label>
        <input name="name" value={form.name} onChange={handleChange} placeholder="如 Airbnb 海景房" />

        <label>地址：</label>
        <input name="address" value={form.address} onChange={handleChange} placeholder="如 上海浦东新区XXX" />

        <label>入住日期：</label>
        <input name="checkIn" type="date" value={form.checkIn} onChange={handleChange} />

        <label>退房日期：</label>
        <input name="checkOut" type="date" value={form.checkOut} onChange={handleChange} />

        <label>跳转链接（可选）：</label>
        <input name="bookingUrl" value={form.bookingUrl} onChange={handleChange} placeholder="https://airbnb.com/..." />

        <label>住宿截图：</label>
        <input id="imageInput" type="file" accept="image/*" onChange={handleFileChange} />

        <button onClick={handleSubmit} disabled={loading}>
          {loading ? "上传中..." : "上传住宿信息"}
        </button>
      </div>
    </>
  );
}
