import React, { useEffect, useState } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./TripPhoto.css";

export default function TripPhotoPage() {
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [selectedDayIndex, setSelectedDayIndex] = useState(1);
  const [photos, setPhotos] = useState([]);
  const [selectedPhoto, setSelectedPhoto] = useState(null); // 🆕 控制大图预览

  useEffect(() => {
    const fetchTrips = async () => {
      const userId = localStorage.getItem("userId");
      if (!userId) return;

      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${userId}`);
        const data = await res.json();
        setTrips(data);
      } catch (err) {
        console.error("❌ 获取旅行失败", err);
        alert("获取旅行列表失败");
      }
    };
    fetchTrips();
  }, []);

  useEffect(() => {
    if (!selectedTrip) return;

    const fetchPhotos = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/photo/trip/${selectedTrip.id}`);
        const data = await res.json();
        setPhotos(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("❌ 获取照片失败", err);
        alert("获取照片失败");
        setPhotos([]);
      }
    };
    fetchPhotos();
  }, [selectedTrip]);

  const days = selectedTrip
    ? Math.ceil((new Date(selectedTrip.endDate) - new Date(selectedTrip.startDate)) / (1000 * 60 * 60 * 24)) + 1
    : 0;

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="trip-photo-page">
        <h2 className="page-title">📷 My Travel Footprints</h2>

        {/* Trip Dropdown */}
        <div className="trip-dropdown">
          <label>Choose Trip:</label>
          <select onChange={(e) => {
            const trip = trips.find((t) => t.id === parseInt(e.target.value));
            setSelectedTrip(trip || null);
            setSelectedDayIndex(1);
          }}>
            <option value="">-- Select a Trip --</option>
            {trips.map((t) => (
              <option key={t.id} value={t.id}>{t.fromCity} → {t.destination}</option>
            ))}
          </select>
        </div>

        {/* Day Selector */}
        {selectedTrip && (
          <div className="day-selector">
            <label>Select Day:</label>
            <select value={selectedDayIndex} onChange={(e) => setSelectedDayIndex(parseInt(e.target.value))}>
              {Array.from({ length: days }, (_, i) => (
                <option key={i} value={i + 1}>Day {i + 1}</option>
              ))}
            </select>
          </div>
        )}

        {/* Photo Display + Upload */}
        {selectedTrip && (
          <div className="photo-card">
            <div className="photo-card-content">
              <div className="left-column">
                <h3 className="day-title">📅 Day {selectedDayIndex}</h3>
                <div className="photo-grid">
                  {photos.filter((p) => p.dayIndex === selectedDayIndex).map((p) => (
                    <div key={p.id} className="photo-item" onClick={() => setSelectedPhoto(p)}>
                      <img src={p.imageUrl} alt={p.placeName} />
                      <p>{p.placeName}</p>
                    </div>
                  ))}
                </div>
              </div>
              <div className="right-column">
                <UploadPhotoForm
                  tripId={selectedTrip.id}
                  dayIndex={selectedDayIndex}
                  onUpload={(photo) => setPhotos((prev) => [...prev, photo])}
                />
              </div>
            </div>
          </div>
        )}

        {/* 🆕 Modal for Big Image */}
        {selectedPhoto && (
          <div className="modal-overlay" onClick={() => setSelectedPhoto(null)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <img src={selectedPhoto.imageUrl} alt="Full" className="modal-image" />
              <h3>{selectedPhoto.placeName}</h3>
              <p>{selectedPhoto.description || "No description provided."}</p>
            </div>
          </div>
        )}
      </div>
    </>
  );
}

function UploadPhotoForm({ tripId, dayIndex, onUpload }) {
  const [files, setFiles] = useState([]);
  const [placeName, setPlaceName] = useState("");
  const [description, setDescription] = useState("");
  const [uploading, setUploading] = useState(false);

  const handleUpload = async () => {
    const userId = localStorage.getItem("userId");
    if (!userId || !tripId || !dayIndex || files.length === 0) {
      alert("请填写完整信息并选择照片");
      return;
    }

    setUploading(true);
    try {
      for (const file of files) {
        const formData = new FormData();
        formData.append("photo", file);
        formData.append("tripId", tripId);
        formData.append("uploadedBy", userId);
        formData.append("dayIndex", dayIndex);
        formData.append("placeName", placeName);
        formData.append("description", description);

        const res = await fetch("http://localhost:3001/api/photo", {
          method: "POST",
          body: formData,
        });

        if (!res.ok) throw new Error(await res.text());

        const newPhoto = await res.json();
        onUpload(newPhoto);
      }

      setFiles([]);
      setPlaceName("");
      setDescription("");
      alert("上传成功！");
    } catch (err) {
      console.error("❌ 上传失败:", err);
      alert("上传失败：" + err.message);
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-form-card">
      <h4>📤 Upload New Photo</h4>
      <input type="file" multiple accept="image/*" onChange={(e) => setFiles(Array.from(e.target.files))} />
      <input type="text" placeholder="📍 Place Name" value={placeName} onChange={(e) => setPlaceName(e.target.value)} />
      <input type="text" placeholder="📝 Description" value={description} onChange={(e) => setDescription(e.target.value)} />
      <button onClick={handleUpload} disabled={!files.length || uploading}>
        {uploading ? "Uploading..." : "Upload"}
      </button>
    </div>
  );
}
