import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css";
import Navbar from "./Navbar";

const SummaryCard = ({ city, departureTime, duration, coverImage, places, onClick }) => {
  return (
    <div className="summary-card" onClick={onClick}>
      <div className="summary-header">
        <div className="summary-title">{city}</div>
        <div className="summary-subinfo">
          <span>{departureTime}</span>・<span>{duration}</span>
        </div>
      </div>
      <div className="summary-body">
        <div className="summary-place-list">
          {places.map((p, idx) => (
            <div key={idx} className="place-line">
              <span className="place-time">{p.time}</span>
              <span className="place-title">{p.title}</span>
            </div>
          ))}
        </div>
        <div className="summary-image-box">
          <img src={coverImage || "/images/default-cover.jpg"} className="summary-cover" />
        </div>
      </div>
    </div>
  );
};


export default function TravelSummaries() {
  const navigate = useNavigate();
  const [cards, setCards] = useState([]);

  useEffect(() => {
    const stored = localStorage.getItem("myPlan");
    if (stored) {
      try {
        const parsed = JSON.parse(stored);
        const grouped = [];

        parsed.schedule?.forEach((day) => {
          day.items.forEach((item) => {
            const city = item.locationName || item.title || "Unknown";
            let existing = grouped.find(g => g.city === city);
            if (!existing) {
              existing = {
                city,
                departureTime: day.date + " " + item.time,
                duration: "1天", // 你可以自定义更好的逻辑
                coverImage: "/images/default-cover.jpg",
                places: []
              };
              grouped.push(existing);
            }
            existing.places.push({ time: item.time, title: item.title });
          });
        });

        setCards(grouped);
      } catch (err) {
        console.error("Failed to parse schedule:", err);
      }
    }
  }, []);

  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">🌐 我的所有行程</h2>
        {cards.length === 0 ? (
          <p style={{ padding: "1rem" }}>No trips found.</p>
        ) : (
          cards.map((card, idx) => (
            <SummaryCard
              key={idx}
              city={card.city}
              departureTime={card.departureTime}
              duration={card.duration}
              places={card.places}
              coverImage={card.coverImage}
              onClick={() => navigate(`/plan-summary`)}
            />
          ))
        )}
      </div>
    </>
  );
}
