import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import AccommodationForm from "./AccommodationForm";
import "./Accommodation.css";

export default function AddAccommodationPage() {
  const { id } = useParams(); // 当前用户 ID
  const [tripList, setTripList] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [accommodations, setAccommodations] = useState([]);

  // 额外状态：编辑 / 删除 / 保存
  const [editing, setEditing] = useState(null); // {id, tripId, name, address, checkIn, checkOut, bookingUrl, imageUrl, imageKey, _newFile?}
  const [saving, setSaving] = useState(false);
  const [deletingId, setDeletingId] = useState(null);

  // 获取当前用户所有行程
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const data = await res.json();
        if (Array.isArray(data) && data.length > 0) {
          setTripList(data);
          setSelectedTripId(data[0].id);
        } else {
          setTripList([]);
          setSelectedTripId(null);
        }
      } catch (err) {
        console.error("❌ 获取行程失败:", err);
      }
    };
    fetchTrips();
  }, [id]);

  // 获取某个行程的住宿列表
  const loadAccommodations = async (tripId) => {
    try {
      const res = await fetch(`http://localhost:3001/api/accommodations?tripId=${tripId}`);
      const data = await res.json();
      setAccommodations((data || []).sort((a, b) => new Date(a.checkIn) - new Date(b.checkIn)));
    } catch (err) {
      console.error("❌ 获取住宿失败:", err);
    }
  };

  // 当选中的 tripId 变化时重新加载住宿
  useEffect(() => {
    if (selectedTripId) loadAccommodations(selectedTripId);
  }, [selectedTripId]);

  // 复用直传 S3 的函数（用于“修改时换图”）
  async function uploadToS3(file, { userId, tripId }) {
    const r1 = await fetch("http://localhost:3001/api/upload/sign", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ contentType: file.type, userId, tripId }),
    });
    if (!r1.ok) throw new Error("获取签名失败：" + (await r1.text().catch(() => "")));
    const { uploadUrl, publicUrl, key } = await r1.json();

    const r2 = await fetch(uploadUrl, {
      method: "PUT",
      headers: { "Content-Type": file.type },
      body: file,
    });
    if (!r2.ok) throw new Error("上传到 S3 失败：" + (await r2.text().catch(() => "")));

    return { publicUrl, key };
  }

  // 删除
  const handleDelete = async (accId, tripId) => {
    if (!window.confirm("确定删除这条住宿吗？")) return;
    try {
      setDeletingId(accId);
      const r = await fetch(`http://localhost:3001/api/accommodations/${accId}`, {
        method: "DELETE",
      });
      if (!r.ok) throw new Error("删除失败");
      await loadAccommodations(tripId);
    } catch (e) {
      alert(e.message || "删除失败");
    } finally {
      setDeletingId(null);
    }
  };

  // 进入编辑
  const startEdit = (acc) => {
    setEditing({
      ...acc,
      // 确保是字符串，给 <input type="date"> 用
      checkIn: acc.checkIn ? String(acc.checkIn) : "",
      checkOut: acc.checkOut ? String(acc.checkOut) : "",
      _newFile: null,
    });
  };

  // 保存编辑
  const saveEdit = async () => {
    if (!editing) return;
    try {
      setSaving(true);

      let imageUrl = editing.imageUrl || null;
      let imageKey = editing.imageKey || null;

      // 如选择了新图，先直传 S3
      if (editing._newFile) {
        const { publicUrl, key } = await uploadToS3(editing._newFile, {
          userId: id,
          tripId: editing.tripId || selectedTripId,
        });
        imageUrl = publicUrl;
        imageKey = key;
      }

      const r = await fetch(`http://localhost:3001/api/accommodations/${editing.id}`, {
        method: "PATCH",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          name: editing.name,
          address: editing.address,
          checkIn: editing.checkIn,
          checkOut: editing.checkOut,
          bookingUrl: editing.bookingUrl,
          imageUrl,
          imageKey, // 后端有字段就用；没有字段也没关系（后端忽略）
        }),
      });
      if (!r.ok) throw new Error(await r.text());

      setEditing(null);
      await loadAccommodations(selectedTripId);
    } catch (e) {
      alert("更新失败：" + (e.message || "未知错误"));
    } finally {
      setSaving(false);
    }
  };

  return (
    <>
      <Navbar />
      <SubNavBar />

      <div className="accommodation-page">
        {/* 行程选择 */}
        <div className="trip-selector-global">
          <label>Choose Trip：</label>
          <select
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            {tripList.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} ➝ {trip.destination}
              </option>
            ))}
          </select>
        </div>

        <div className="accommodation-columns">
          {/* 左侧时间轴 */}
          <div className="timeline-column">
            <h3>Accommodations</h3>
            {accommodations.length === 0 ? (
              <p>No accommodation yet</p>
            ) : (
              accommodations.map((acc) => {
                const isEditing = editing?.id === acc.id;
                return (
                  <div className="accommodation-card" key={acc.id}>
                    <div className="date-label">
                      {new Date(acc.checkIn).toLocaleDateString()} → {new Date(acc.checkOut).toLocaleDateString()}
                    </div>

                    <div style={{ display: "flex", gap: "1rem", alignItems: "center" }}>
                      <img
                        src={
                          acc.imageUrl?.startsWith("http")
                            ? acc.imageUrl // ✅ S3 直链
                            : `http://localhost:3001${acc.imageUrl || ""}` // 兼容老数据
                        }
                        alt="accommodation"
                        className="thumbnail"
                      />

                      {/* 信息区：展示 或 编辑表单 */}
                      {!isEditing ? (
                        <div className="card-info" style={{ flex: 1 }}>
                          <h4>{acc.name || "未命名住宿"}</h4>
                          <p>{acc.address || "暂无地址"}</p>
                          {acc.bookingUrl && (
                            <a href={acc.bookingUrl} target="_blank" rel="noreferrer">
                              🔗 Link
                            </a>
                          )}
                        </div>
                      ) : (
                        <div style={{ flex: 1, display: "grid", gap: 6 }}>
                          <input
                            value={editing.name || ""}
                            onChange={(e) => setEditing({ ...editing, name: e.target.value })}
                            placeholder="🏨 Name"
                          />
                          <input
                            value={editing.address || ""}
                            onChange={(e) => setEditing({ ...editing, address: e.target.value })}
                            placeholder="📍 Address"
                          />
                          <div style={{ display: "flex", gap: 8 }}>
                            <input
                              type="date"
                              value={editing.checkIn?.slice(0, 10) || ""}
                              onChange={(e) => setEditing({ ...editing, checkIn: e.target.value })}
                            />
                            <input
                              type="date"
                              value={editing.checkOut?.slice(0, 10) || ""}
                              onChange={(e) => setEditing({ ...editing, checkOut: e.target.value })}
                            />
                          </div>
                          <input
                            value={editing.bookingUrl || ""}
                            onChange={(e) => setEditing({ ...editing, bookingUrl: e.target.value })}
                            placeholder="🔗 Link"
                          />
                          <input
                            type="file"
                            accept="image/*"
                            onChange={(e) =>
                              setEditing({ ...editing, _newFile: e.target.files?.[0] || null })
                            }
                          />
                        </div>
                      )}

                      {/* 右侧操作区 */}
                      <div style={{ display: "flex", gap: 8 }}>
                        {!isEditing ? (
                          <>
                            <button onClick={() => startEdit(acc)}>Edit</button>
                            <button
                              className="btn-danger"
                              onClick={() => handleDelete(acc.id, acc.tripId)}
                              disabled={deletingId === acc.id}
                            >
                              {deletingId === acc.id ? "Deleting..." : "Delete"}
                            </button>
                          </>
                        ) : (
                          <>
                            <button onClick={saveEdit} disabled={saving}>
                              {saving ? "Saving..." : "Save"}
                            </button>
                            <button onClick={() => setEditing(null)}>Cancel</button>
                          </>
                        )}
                      </div>
                    </div>
                  </div>
                );
              })
            )}
          </div>

          {/* 右侧上传表单（新增） */}
          <div className="form-column">
            <AccommodationForm
              userId={id}
              tripList={tripList}
              selectedTripId={selectedTripId}
              setSelectedTripId={setSelectedTripId}
              onSuccess={() => loadAccommodations(selectedTripId)}
            />
          </div>
        </div>
      </div>
    </>
  );
}
