// AIPlan.js
import React, { useState, useEffect } from 'react';
import { useLocation } from 'react-router-dom';
import ReactMarkdown from 'react-markdown';
import './AIPlan.css';

function AIPlan() {
  const location = useLocation();
  const planData = location.state || {}; // 来自 CreatePlan
  const [plan, setPlan] = useState('');
  const [preference, setPreference] = useState('');
console.log("Received planData:", planData);

  const generatePlan = async () => {
    const days = calculateDays(planData.startDate, planData.endDate);
    try {
      const response = await fetch('http://localhost:3001/api/plan', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          fromCity: planData.fromCity,
          toCity: planData.destination,
          days,
          startDate: planData.startDate,
          endDate: planData.endDate,
          preferences: preference,
        }),
      });
      const data = await response.json();
      setPlan(data.plan);
    } catch (err) {
      console.error('Error generating plan:', err);
    }
  };

  const calculateDays = (start, end) => {
    const s = new Date(start);
    const e = new Date(end);
    const diff = (e - s) / (1000 * 60 * 60 * 24) + 1;
    return Math.max(diff, 1);
  };

  const handlePreferenceChange = (e) => {
    setPreference(e.target.value);
  };

  const handleGenerateClick = () => {
    if (!preference) {
      alert("Please select a preference before generating your plan.");
      return;
    }
    generatePlan();
  };

  return (
    <div className="app-container">
      <h1>AI Travel Planner</h1>
      <p>
        <strong>{planData.fromCity} → {planData.destination}</strong><br />
        Dates: <strong>{planData.startDate} → {planData.endDate}</strong><br />
        {/* Participants: <strong>{planData.participants}</strong> */}
      </p>

      <select value={preference} onChange={handlePreferenceChange}>
        <option value="">Select Preference</option>
        <option value="nature">Nature</option>
        <option value="history">History</option>
        <option value="food">Food</option>
      </select>
      <button onClick={handleGenerateClick}>Generate Plan</button>

      {plan && (
        <div className="plan-output">
          <ReactMarkdown>{plan}</ReactMarkdown>
        </div>
      )}
    </div>
  );
}

export default AIPlan;
