// src/pages/Home.js
import React from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Features from './Features';


function Home() {
  const navigate = useNavigate();

  const handleCreatePlan = () => {
    navigate('/create'); // 之后我们创建这个路由
  };

  return (
    <>
    <div className="home-container">
      <nav className="navbar">
        <div className="logo">GoGoGlobe</div>
        <div className="nav-links">
          <a href="/plans">My Plan</a>
          <a href="/logout">Sign Out</a>
        </div>
      </nav>

      <div className="main-content">
        <div className="left-panel">
          <h1>Tired of chaotic travel planning?</h1>
          <h2><span className="highlight">Plan together.</span> Remember forever.</h2>
          <button onClick={handleCreatePlan}>创建新的旅行计划</button>
        </div>

        <div className="right-panel">
          <img src="/images/home-illustration.png" alt="illustration" />
        </div>
      </div>
    </div>
    <Features />
    </>
  );
}

export default Home;
