import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./Accommodation.css";

export default function AddAccommodationPage() {
  const { id } = useParams(); // ÂΩìÂâçÁî®Êà∑ ID
  const [tripList, setTripList] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);

  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const data = await res.json();

        if (Array.isArray(data) && data.length > 0) {
          setTripList(data);
          setSelectedTripId(data[0].id); // ÈªòËÆ§ÈÄâÁ¨¨‰∏Ä‰∏™ trip
        }
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñË°åÁ®ãÂ§±Ë¥•:", err);
      }
    };

    fetchTrips();
  }, [id]);

  return (
    <>
      <Navbar />
      <SubNavBar />

      <div className="add-accommodation-page">
        <h2 className="title">üè† ‰∏ä‰º†‰ΩèÂÆø‰ø°ÊÅØ</h2>

        <div className="trip-selector">
          <label>ÈÄâÊã©Ë°åÁ®ãÔºö</label>
          <select
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            {tripList.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} ‚ûù {trip.destination}
              </option>
            ))}
          </select>
        </div>

        {selectedTripId ? (
          <AddAccommodationForm tripId={selectedTripId} />
        ) : (
          <p className="no-trip-msg">ËØ∑ÂÖàÈÄâÊã©Ë°åÁ®ã</p>
        )}
      </div>
    </>
  );
}
