import React, { useState, useEffect } from "react";
import "./PackingList.css";
import Navbar from "./Navbar";

const initialPackingData = [
  {
    id: 1,
    title: "San Jose ➝ Sequoia Nation Park",
    date: "6/5/2025 ~ 6/7/2025",
    days: 3,
    items: [
      { name: "登山鞋", packed: false },
      { name: "帽子", packed: false },
      { name: "防晒霜", packed: false },
      { name: "驱蚊液", packed: false },
      { name: "水瓶", packed: false },
      { name: "相机", packed: false },
    ],
  },
  {
    id: 2,
    title: "San Jose ➝ Seattle",
    date: "8/8/2025 ~ 8/15/2025",
    days: 8,
    items: [
      { name: "行李箱", packed: false },
      { name: "衣物 x8", packed: false },
      { name: "洗漱包", packed: false },
      { name: "充电宝", packed: false },
      { name: "雨衣", packed: false },
      { name: "运动鞋", packed: false },
    ],
  },
];

export default function PackingList() {
  const [packingData, setPackingData] = useState([]);
  const [newItems, setNewItems] = useState({});

  // ✅ 从 localStorage 初始化
  useEffect(() => {
    const stored = localStorage.getItem("packingData");
    if (stored) {
      setPackingData(JSON.parse(stored));
    } else {
      setPackingData(initialPackingData);
    }
  }, []);

  // ✅ 每次更新都保存到 localStorage
  useEffect(() => {
    localStorage.setItem("packingData", JSON.stringify(packingData));
  }, [packingData]);

  const togglePacked = (tripId, index) => {
    const updated = packingData.map(trip => {
      if (trip.id === tripId) {
        const updatedItems = [...trip.items];
        updatedItems[index].packed = !updatedItems[index].packed;
        return { ...trip, items: updatedItems };
      }
      return trip;
    });
    setPackingData(updated);
  };

  const handleAddItem = (tripId) => {
    if (!newItems[tripId]?.trim()) return;
    const updated = packingData.map(trip => {
      if (trip.id === tripId) {
        const updatedItems = [...trip.items, { name: newItems[tripId], packed: false }];
        return { ...trip, items: updatedItems };
      }
      return trip;
    });
    setPackingData(updated);
    setNewItems(prev => ({ ...prev, [tripId]: "" }));
  };

  return (
    <>
      <Navbar />
      <div className="packing-page">
        {packingData.map((trip) => (
          <div className="packing-card" key={trip.id}>
            <div className="packing-header">
              <h3>{trip.title}</h3>
              <span className="packing-date">{trip.date} ・{trip.days}天</span>
            </div>

            <ul className="packing-items">
              {trip.items.map((item, idx) => (
                <li key={idx}>
                  <input
                    type="checkbox"
                    checked={item.packed}
                    onChange={() => togglePacked(trip.id, idx)}
                  />
                  <span style={{ textDecoration: item.packed ? "line-through" : "none" }}>
                    {item.name}
                  </span>
                </li>
              ))}
            </ul>

            <div className="add-item-section">
              <input
                type="text"
                placeholder="添加新物品..."
                value={newItems[trip.id] || ""}
                onChange={(e) =>
                  setNewItems({ ...newItems, [trip.id]: e.target.value })
                }
              />
              <button onClick={() => handleAddItem(trip.id)}>添加</button>
            </div>
          </div>
        ))}
      </div>
    </>
  );
}
