// App.js
import React, { useState } from 'react';
import axios from 'axios';
import './App.css';

function App() {
  const [city, setCity] = useState('');
  const [days, setDays] = useState('');
  const [preferences, setPreferences] = useState('');
  const [result, setResult] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault(); // Èò≤Ê≠¢È°µÈù¢Âà∑Êñ∞
    try {
      const response = await axios.post('http://localhost:3001/api/plan', {
        city,
        days,
        preferences,
      });
      setResult(response.data.plan); // ÂÅáËÆæÂêéÁ´ØËøîÂõûÁöÑÊòØ { plan: "..." }
    } catch (error) {
      console.error('Failed to generate plan:', error);
    }
  };

  return (
    <div className="app-container">
      <h1>‚úàÔ∏è AI Travel Planner</h1>
      <form onSubmit={handleSubmit}>
        <input
          type="text"
          placeholder="Enter a city"
          value={city}
          onChange={(e) => setCity(e.target.value)}
        />
        <input
          type="number"
          placeholder="Number of days"
          value={days}
          onChange={(e) => setDays(e.target.value)}
        />
        <select
          value={preferences}
          onChange={(e) => setPreferences(e.target.value)}
        >
          <option value="">Choose a preference</option>
          <option value="nature">Nature</option>
          <option value="history">History</option>
          <option value="food">Food</option>
        </select>
        <button type="submit">Generate Plan</button>
      </form>

      {result && (
        <div className="result">
          <h2>üó∫Ô∏è Suggested Itinerary</h2>
          <pre>{result}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
