import React, { useState } from 'react';
import './Login.css';
import { useNavigate, Link } from 'react-router-dom';

const API_BASE =
  (typeof import.meta !== 'undefined' && import.meta.env?.VITE_API_BASE) ||
  process.env.REACT_APP_API_BASE ||
  'http://localhost:3001';

export default function Login() {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [message, setMessage] = useState('');
  const navigate = useNavigate();

  const handleLogin = async (e) => {
    e.preventDefault();
    setMessage(''); // 清空提示
    try {
      // 1) 先登录（一定要带 credentials）
      const resp = await fetch(`${API_BASE}/api/auth/login`, {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        credentials: 'include',
        body: JSON.stringify({ email, password }),
      });
      const data = await resp.json();

      if (!resp.ok) {
        setMessage(`❌ 登录失败：${data?.error || 'unknown error'}`);
        return;
      }

      // 2) 登录成功后，再确认一次登录态并拿到 id
      const me = await fetch(`${API_BASE}/api/auth/me`, { credentials: 'include' })
        .then(r => r.ok ? r.json() : { user: null });

      const id = me?.user?.id ?? data?.userId; // 兼容两种返回
      if (id) localStorage.setItem('userId', String(id));

      setMessage('✅ 登录成功！');
      navigate('/'); // 回到首页（你的路由里首页是 "/"）
    } catch (err) {
      console.error(err);
      setMessage('❌ 网络异常，请稍后重试');
    }
  };

  const handleGoogleSignIn = () => {
    window.location.href = `${API_BASE}/api/auth/google`; // 不要写死 ngrok
  };

  return (
    <div className="login-wrapper">
      <div className="login-card">
        <h1>Welcome GoGoGlobe！</h1>
        <p>Plan smart. Travel far. Remember forever.</p>
        <form onSubmit={handleLogin}>
          <input
            type="email"
            placeholder="E-mail Address"
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <input
            type="password"
            placeholder="Password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
          <button type="submit">Sign In</button>
        </form>
        <div className="message">{message}</div>
        <div className="divider">or</div>
        <div className="google-login" onClick={handleGoogleSignIn}>
          <img src="/images/web_light_rd_SI@1x.png" alt="Google" className="google-icon" />
        </div>
        <div className="register-hint">
          Do not have account？<Link to="/register">Create free account</Link>
        </div>
      </div>
    </div>
  );
}
