import { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";

export default function SplitwisePage() {
  const { id } = useParams(); // ÂΩìÂâçÁî®Êà∑ ID
  const [tripData, setTripData] = useState([]); // Áî®Êà∑ÊâÄÊúâË°åÁ®ã
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [memberData, setMemberData] = useState([]); // ÂΩìÂâçË°åÁ®ãÁöÑÊàêÂëò
  const [expenses, setExpenses] = useState([]); // ÂΩìÂâçË°åÁ®ãÁöÑË¥πÁî®È°π

  // Ëé∑ÂèñÁî®Êà∑ÁöÑÊâÄÊúâË°åÁ®ã
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const trips = await res.json();
        if (Array.isArray(trips) && trips.length > 0) {
          setTripData(trips);
          setSelectedTripId(trips[0].id); // ÈªòËÆ§ÈÄâÁ¨¨‰∏Ä‰∏™Ë°åÁ®ã
        }
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñË°åÁ®ãÂ§±Ë¥•:", err);
      }
    };

    fetchTrips();
  }, [id]);

  // Ê†πÊçÆÈÄâ‰∏≠ÁöÑ tripId Ëé∑ÂèñÊàêÂëòÂíåË¥πÁî®‰ø°ÊÅØ
  useEffect(() => {
    if (!selectedTripId) return;

    const fetchMembers = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/members?tripId=${selectedTripId}`);
        const members = await res.json();
        setMemberData(members);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñÊàêÂëòÂ§±Ë¥•:", err);
      }
    };

    const fetchExpenses = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/expenses?tripId=${selectedTripId}`);
        const data = await res.json();
        setExpenses(data);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñË¥πÁî®Â§±Ë¥•:", err);
      }
    };

    fetchMembers();
    fetchExpenses();
  }, [selectedTripId]);

  // Ê∑ªÂä†Êñ∞Ë¥πÁî®È°π
  const handleAddExpense = () => {
    setExpenses([...expenses, { name: "", amount: "", paidBy: "" }]);
  };

  // ‰øÆÊîπË¥πÁî®È°πÂÜÖÂÆπ
  const handleChange = (index, field, value) => {
    const updated = [...expenses];
    updated[index][field] = value;
    setExpenses(updated);
  };

  // ‰øùÂ≠òË¥πÁî®È°π
  const handleSave = async () => {
    try {
      const res = await fetch("http://localhost:3001/api/expenses/save", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ tripId: selectedTripId, expenses }),
      });

      if (res.ok) {
        alert("üíæ ‰øùÂ≠òÊàêÂäüÔºÅ");
      } else {
        throw new Error("‰øùÂ≠òÂ§±Ë¥•");
      }
    } catch (err) {
      console.error("‚ùå ‰øùÂ≠òÂ§±Ë¥•:", err);
      alert("‚ùå ‰øùÂ≠òÂ§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÊéßÂà∂Âè∞");
    }
  };

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="splitwise-page">
        <div className="trip-selector">
          <label htmlFor="tripSelect">ÈÄâÊã©Ë°åÁ®ãÔºö</label>
          <select
            id="tripSelect"
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            <option value="">ÈÄâÊã©Ë°åÁ®ã</option>
            {tripData.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} ‚Üí {trip.destination}
              </option>
            ))}
          </select>
        </div>

        <div className="splitwise-card">
          <h3>Ë¥πÁî®ÂàÜÊëä</h3>
          {expenses.map((item, index) => (
            <div className="budget-item" key={index}>
              <input
                value={item.name}
                onChange={(e) => handleChange(index, "name", e.target.value)}
                placeholder="È°πÁõÆÂêçÁß∞"
              />
              <input
                type="number"
                value={item.amount}
                onChange={(e) => handleChange(index, "amount", e.target.value)}
                placeholder="ÈáëÈ¢ù"
              />
              <select
                value={item.paidBy}
                onChange={(e) => handleChange(index, "paidBy", e.target.value)}
              >
                <option value="">ÈÄâÊã©‰ªòÊ¨æ‰∫∫</option>
                {memberData.map((member) => (
                  <option key={member.id} value={member.user?.email}>
                    {member.user?.name || member.user?.email}
                  </option>
                ))}
              </select>
            </div>
          ))}

          <button onClick={handleAddExpense}>‚ûï Ê∑ªÂä†Ë¥πÁî®</button>
          <button onClick={handleSave} className="save-btn">üíæ ‰øùÂ≠ò</button>
        </div>
      </div>
    </>
  );
}
