import React, { useState } from "react";
import "./AccommodationForm.css";

export default function AccommodationForm({
  userId,
  tripList,
  selectedTripId,
  setSelectedTripId,
  onSuccess,
}) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleFileChange = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    const allow = ["image/jpeg", "image/png", "image/webp"];
    if (!allow.includes(f.type)) return alert("仅支持 JPG/PNG/WebP");
    if (f.size > 10 * 1024 * 1024) return alert("文件不得超过 10MB");
    setImageFile(f);
    setPreview(URL.createObjectURL(f));
  };

  // 直传 S3
  async function uploadToS3(file, { userId, tripId }) {
    // 1) 向后端要预签名 URL
    const r1 = await fetch("http://localhost:3001/api/upload/sign", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({ contentType: file.type, userId, tripId }),
    });
    if (!r1.ok) {
      const msg = await r1.text().catch(() => "");
      throw new Error("获取签名失败：" + msg);
    }
    const { uploadUrl, publicUrl, key } = await r1.json();

    // 2) 用预签名地址直接 PUT 到 S3（Content-Type 要和签名一致）
    const r2 = await fetch(uploadUrl, {
      method: "PUT",
      headers: { "Content-Type": file.type },
      body: file,
    });
    if (!r2.ok) {
      const msg = await r2.text().catch(() => "");
      throw new Error("上传到 S3 失败：" + msg);
    }
    return { publicUrl, key };
  }

  const handleSubmit = async () => {
    if (!form.name || !form.checkIn || !form.checkOut) return alert("请填写完整信息");
    if (!selectedTripId) return alert("请选择行程");

    try {
      setLoading(true);

      // ① 图片先上传到 S3
      let imageUrl = "";
      let imageKey = "";
      if (imageFile) {
        const { publicUrl, key } = await uploadToS3(imageFile, {
          userId,
          tripId: selectedTripId,
        });
        imageUrl = publicUrl;
        imageKey = key;
      }

      // ② 把表单 + S3 地址存数据库
      const res = await fetch("http://localhost:3001/api/accommodations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          tripId: selectedTripId,
          name: form.name,
          address: form.address,
          checkIn: form.checkIn,
          checkOut: form.checkOut,
          bookingUrl: form.bookingUrl,
          imageUrl,   // 存 S3 完整 URL
          imageKey,   // 也存 key，后续想换 CDN/签名下载会用到
        }),
      });

      const data = await res.json().catch(() => ({}));
      if (!res.ok || !data?.id) throw new Error(data?.message || "保存失败");

      alert("✅ 上传成功");
      setForm({ name: "", address: "", checkIn: "", checkOut: "", bookingUrl: "" });
      setImageFile(null);
      setPreview("");
      onSuccess && onSuccess();
    } catch (err) {
      console.error(err);
      alert("❌ 失败：" + (err.message || "未知错误"));
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="form-card">
      <h3 className="accommodation-title">Add New Accommodation</h3>

      {/* Trip 下拉（需要的话可以在表单内切换） */}
      {tripList?.length > 0 && (
        <select
          value={selectedTripId ?? ""}
          onChange={(e) => setSelectedTripId(Number(e.target.value))}
          style={{ marginBottom: 8 }}
        >
          {tripList.map((t) => (
            <option key={t.id} value={t.id}>
              {t.fromCity} ➝ {t.destination}
            </option>
          ))}
        </select>
      )}

      <input name="name" value={form.name} onChange={handleChange} placeholder="🏨 Accommodation Name" />
      <input name="address" value={form.address} onChange={handleChange} placeholder="📍 Address" />
      <input name="checkIn" type="date" value={form.checkIn} onChange={handleChange} />
      <input name="checkOut" type="date" value={form.checkOut} onChange={handleChange} />
      <input name="bookingUrl" value={form.bookingUrl} onChange={handleChange} placeholder="🔗 Link" />

      <input type="file" accept="image/*" onChange={handleFileChange} />
      {preview && (
        <img
          src={preview}
          alt="preview"
          style={{ width: 240, height: 160, objectFit: "cover", borderRadius: 8, marginTop: 8 }}
        />
      )}

      <button onClick={handleSubmit} disabled={loading}>
        {loading ? "上传中..." : "Upload"}
      </button>
    </div>
  );
}
