import React, { useEffect, useState } from "react";
import axios from "axios";
import TripPackingCard from "./TripPackingCard";
import "./PackingList.css";

export default function PackingListPage({ userId }) {
  const [packingLists, setPackingLists] = useState([]);

  useEffect(() => {
    axios.get(`/api/packing-lists?userId=${userId}`).then(res => {
      setPackingLists(res.data);
    });
  }, [userId]);

  const handleUpdate = (tripId, updatedItems) => {
    axios.put(`/api/packing-lists/${tripId}`, { items: updatedItems }).then(() => {
      setPackingLists(prev =>
        prev.map(t => t.id === tripId ? { ...t, items: updatedItems } : t)
      );
    });
  };

  return (
    <div className="packing-page">
      <h2>ðŸ“¦ My Packing Lists</h2>
      {packingLists.map(trip => (
        <TripPackingCard
          key={trip.id}
          trip={trip}
          onUpdate={handleUpdate}
        />
      ))}
    </div>
  );
}
