import React, { useState } from "react";

export default function AddAccommodationForm({ tripId, onSuccess }) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
    imageUrl: "",
  });

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async () => {
    try {
      const res = await fetch("http://localhost:3001/api/accommodations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ ...form, tripId }),
      });
      const data = await res.json();
      if (data.id) {
        alert("✅ 添加成功");
        onSuccess?.(); // 可选刷新数据
        setForm({
          name: "",
          address: "",
          checkIn: "",
          checkOut: "",
          bookingUrl: "",
          imageUrl: "",
        });
      } else {
        alert("❌ 添加失败");
      }
    } catch (err) {
      console.error("❌ 添加失败", err);
    }
  };

  return (
    <div className="add-accommodation-form">
      <h3>添加住宿</h3>
      <input name="name" value={form.name} onChange={handleChange} placeholder="住宿名称" />
      <input name="address" value={form.address} onChange={handleChange} placeholder="地址" />
      <input name="checkIn" type="date" value={form.checkIn} onChange={handleChange} />
      <input name="checkOut" type="date" value={form.checkOut} onChange={handleChange} />
      <input name="bookingUrl" value={form.bookingUrl} onChange={handleChange} placeholder="跳转链接" />
      <input name="imageUrl" value={form.imageUrl} onChange={handleChange} placeholder="图片链接（可选）" />
      <button onClick={handleSubmit}>添加住宿</button>
    </div>
  );
}
