import React, { useEffect, useState } from "react";
// useParams ‰∏çÂÜçÈúÄË¶Å‰∫ÜÔºö‰ªé‰ºöËØù/Êú¨Âú∞Êãø userId Âç≥ÂèØ
// import { useParams } from "react-router-dom";

const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:3001";

export default function NotificationList() {
  const [notifications, setNotifications] = useState([]);
  const [userId, setUserId] = useState(null);

  // Â∞èÂ∑•ÂÖ∑ÔºöÁªü‰∏ÄÂèñ JSONÔºåÈÅøÂÖçÊää HTML ÂΩì JSON Ëß£Êûê
  const getJSON = async (path, options = {}) => {
    const res = await fetch(`${API_BASE}${path}`, {
      credentials: "include",
      ...options,
    });
    const ct = res.headers.get("content-type") || "";
    if (!ct.includes("application/json")) {
      const body = await res.text();
      throw new Error(`Expected JSON, got ${ct}: ${body.slice(0, 120)}‚Ä¶`);
    }
    if (!res.ok) throw new Error(await res.text().catch(() => `HTTP ${res.status}`));
    return res.json();
  };

  // Áªü‰∏ÄÊãø userIdÔºölocalStorage -> /api/auth/me
  useEffect(() => {
    (async () => {
      let uid = localStorage.getItem("userId");
      if (!uid) {
        try {
          const r = await fetch(`${API_BASE}/api/auth/me`, { credentials: "include" });
          if (r.ok) {
            const d = await r.json().catch(() => null);
            if (d?.user?.id) {
              uid = String(d.user.id);
              localStorage.setItem("userId", uid);
            }
          }
        } catch {}
      }
      if (!uid) return; // Êú™ÁôªÂΩï
      setUserId(Number(uid));

      // ÊãâÈÄöÁü•ÂàóË°®
      try {
        const data = await getJSON(`/api/notification/user/${uid}`);
        setNotifications(Array.isArray(data) ? data : []);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñÈÄöÁü•Â§±Ë¥•:", err);
        setNotifications([]);
      }
    })();
  }, []);

  const markAsRead = async (notificationId) => {
    try {
      await fetch(`${API_BASE}/api/notification/${notificationId}/read`, {
        method: "PATCH",
        credentials: "include",
      });
      setNotifications((prev) =>
        prev.map((n) => (n.id === notificationId ? { ...n, isRead: true } : n))
      );
    } catch (err) {
      console.error("‚ùå Ê†áËÆ∞‰∏∫Â∑≤ËØªÂ§±Ë¥•:", err);
    }
  };

  return (
    <div className="p-6 bg-[#f9fbfd] min-h-screen">
      <h2 className="text-2xl font-bold mb-6">üîî ÈÄöÁü•ÂàóË°®</h2>

      {!userId ? (
        <p className="text-gray-500 text-lg">ËØ∑ÂÖàÁôªÂΩï</p>
      ) : notifications.length === 0 ? (
        <p className="text-gray-500 text-lg">ÊöÇÊó†ÈÄöÁü•</p>
      ) : (
        <div className="space-y-4">
          {notifications.map((n) => (
            <div
              key={n.id}
              className={`bg-white rounded-2xl shadow-sm border px-6 py-4 transition duration-200 ${
                n.isRead ? "border-gray-300" : "border-yellow-400 bg-yellow-50"
              }`}
            >
              <div className="flex justify-between items-start">
                <div className="text-base font-medium text-gray-800">{n.message}</div>
                <div className="text-sm text-gray-500 ml-4 whitespace-nowrap">
                  {new Date(n.createdAt).toLocaleString()}
                </div>
              </div>

              {!n.isRead && (
                <button
                  onClick={() => markAsRead(n.id)}
                  className="mt-4 px-4 py-2 rounded-xl text-white font-semibold hover:opacity-90 transition"
                >
                  Ê†áËÆ∞‰∏∫Â∑≤ËØª
                </button>
              )}
            </div>
          ))}
        </div>
      )}
    </div>
  );
}
