import React, { useEffect, useState } from 'react';
import './MyItinerary.css';

function MyItinerary() {
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const storedPlan = JSON.parse(localStorage.getItem("myPlan"));
    if (storedPlan?.schedule) {
      setSchedule(storedPlan.schedule);
    }
  }, []);

  const handleInputChange = (dayIndex, itemIndex, field, value) => {
    const newSchedule = [...schedule];
    newSchedule[dayIndex].items[itemIndex][field] = value;
    setSchedule(newSchedule);
  };

  const savePlan = () => {
    localStorage.setItem("myPlan", JSON.stringify({ schedule }));
    alert("Plan saved!");
  };

  return (
  <div className="my-itinerary-wrapper">
    {schedule.length === 0 ? (
      <p style={{ textAlign: 'center', marginTop: '2rem' }}>üö´ No itinerary found.</p>
    ) : (
      schedule.map((day, i) => (
        <div key={i} className="day-block">
          <h3 className="day-title">{day.date}</h3>
          <div className="item-list">
            {day.items.map((item, j) => (
              <div className="schedule-item-card" key={j}>
                <div className="item-time">{item.time}</div>
                <div className="item-details">
                  <input
                    className="item-title"
                    value={item.title}
                    onChange={(e) => {
                      const updated = [...schedule];
                      updated[i].items[j].title = e.target.value;
                      setSchedule(updated);
                    }}
                  />
                  <textarea
                    className="item-desc"
                    value={item.description}
                    onChange={(e) => {
                      const updated = [...schedule];
                      updated[i].items[j].description = e.target.value;
                      setSchedule(updated);
                    }}
                  />
                  {item.duration && <div className="item-duration">‚è± {item.duration}</div>}
                </div>
              </div>
            ))}
          </div>
        </div>
      ))
    )}
    <div style={{ textAlign: "center", marginTop: "2rem" }}>
      <button
        className="apply-btn"
        onClick={() => localStorage.setItem("myPlan", JSON.stringify({ schedule }))}
      >
        Save Plan
      </button>
    </div>
  </div>
);
}

export default MyItinerary;
