import React, { useState } from 'react';
import { useLocation } from 'react-router-dom';

function MyItinerary() {
  const location = useLocation();
  const [markdown, setMarkdown] = useState(location.state?.markdown || '');
//   const [schedule, setSchedule] = useState(location.state?.schedule || []);
  const [schedule, setSchedule] = useState([]);

  useEffect(() => {
    const storedPlan = JSON.parse(localStorage.getItem("myPlan"));
    if (storedPlan?.schedule) {
      setSchedule(storedPlan.schedule);
    }
  }, []);
  return (
    <div className="my-itinerary-container">
      <h1>My Itinerary</h1>
      <textarea
        value={markdown}
        onChange={(e) => setMarkdown(e.target.value)}
        rows={20}
        style={{ width: '100%', fontFamily: 'monospace', padding: '1rem' }}
      />
      {/* 你可以根据 schedule 再生成可编辑卡片视图 */}
    </div>
  );
}

export default MyItinerary;
