import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./PackingList.css";

const defaultAvatar = "https://cdn-icons-png.flaticon.com/512/149/149071.png";

export default function MemberPage() {
  const { id } = useParams(); // Áî®Êà∑ ID
  const [tripData, setTripData] = useState([]); // ‚úÖ ‰øùÂ≠òÁî®Êà∑ÁöÑÊâÄÊúâË°åÁ®ã
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [memberData, setMemberData] = useState([]);
  const [newMemberName, setNewMemberName] = useState("");

  // Á¨¨‰∏ÄÊ≠•ÔºöÈÄöËøáÁî®Êà∑ ID Ëé∑ÂèñÊâÄÊúâË°åÁ®ã
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const trips = await res.json();

        if (Array.isArray(trips) && trips.length > 0) {
          setTripData(trips);
          setSelectedTripId(trips[0].id); // ÈªòËÆ§ÈÄâÁ¨¨‰∏Ä‰∏™Ë°åÁ®ã
        }
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñË°åÁ®ãÂ§±Ë¥•:", err);
      }
    };

    fetchTrips();
  }, [id]);

  // Á¨¨‰∫åÊ≠•ÔºöÂΩìÈÄâ‰∏≠Ë°åÁ®ãÂèòÂåñÊó∂ÔºåËé∑ÂèñËØ•Ë°åÁ®ãÁöÑÊàêÂëò
  useEffect(() => {
    if (!selectedTripId) return;

    const fetchMembers = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/members?tripId=${selectedTripId}`);
        const members = await res.json();
        setMemberData(members);
      } catch (err) {
        console.error("‚ùå Ëé∑ÂèñÊàêÂëòÂ§±Ë¥•:", err);
      }
    };

    fetchMembers();
  }, [selectedTripId]);

  // ‰øÆÊîπËßíËâ≤Âπ∂ÂêåÊ≠•ÂêéÁ´Ø
  const handleRoleChange = async (memberId, newRole) => {
    try {
      await fetch(`http://localhost:3001/api/members/${memberId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ role: newRole }),
      });

      setMemberData((prev) =>
        prev.map((m) => (m.id === memberId ? { ...m, role: newRole } : m))
      );
    } catch (err) {
      console.error("‚ùå Êõ¥Êñ∞ËßíËâ≤Â§±Ë¥•:", err);
    }
  };

    // Ê∑ªÂä†Êñ∞ÊàêÂëò
  const handleAddMember = async () => {
  const name = newMemberName.trim();
  if (!name || !selectedTripId) return;

  try {
    const res = await fetch(`http://localhost:3001/api/members`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        name,
        tripId: selectedTripId,
        role: "Member", // ÈªòËÆ§ËßíËâ≤
      }),
    });

    const data = await res.json();

    if (!data || !data.id) {
      alert("‚ùå Ê∑ªÂä†Â§±Ë¥•ÔºåËØ∑Ê£ÄÊü•ÂêéÁ´ØËøîÂõûÊï∞ÊçÆ");
      return;
    }

    // ‚úÖ Ê∑ªÂä†Âà∞ memberData ‰∏≠
    setMemberData((prev) => [...prev, data]);
    setNewMemberName(""); // Ê∏ÖÁ©∫ËæìÂÖ•Ê°Ü
  } catch (err) {
    console.error("‚ùå Ê∑ªÂä†ÊàêÂëòÂ§±Ë¥•:", err);
  }
};

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="packing-page">
        <div className="trip-select">
          <label htmlFor="tripSelect">Êü•ÁúãË°åÁ®ãÔºö</label>
          <select
            id="tripSelect"
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            <option value="">ËØ∑ÈÄâÊã©Ë°åÁ®ã</option>
            {tripData.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} ‚ûù {trip.destination}
              </option>
            ))}
          </select>
        </div>

        <div className="packing-card">
          <div className="packing-header">
            <h3>üë• ÊàêÂëòÂàóË°®</h3>
            <div style={{ marginBottom: "1rem", display: "flex", gap: "8px", alignItems: "center" }}>
  <input
    type="text"
    value={newMemberName}
    onChange={(e) => setNewMemberName(e.target.value)}
    placeholder="ËØ∑ËæìÂÖ•ÊàêÂëòÂßìÂêç"
    style={{
      flex: 1,
      padding: "8px",
      borderRadius: "6px",
      border: "1px solid #ccc",
    }}
  />
  <button
    onClick={handleAddMember}
    // style={{
    //   background: "#007bff",
    //   color: "white",
    //   padding: "8px 12px",
    //   border: "none",
    //   borderRadius: "6px",
    //   cursor: "pointer",
    // }}
  >
    Ê∑ªÂä†ÊàêÂëò
  </button>
</div>

          </div>

          <ul className="packing-items">
            {memberData.map((member) => (
              <li key={member.id} className="packing-item" style={{ display: "flex", alignItems: "center" }}>
                <img
                  src={defaultAvatar}
                  alt="avatar"
                  style={{ width: "36px", height: "36px", borderRadius: "50%", marginRight: "12px" }}
                />
                <div style={{ flex: 1 }}>
                  <strong>{member.name}</strong>
                  <div style={{ fontSize: "14px", color: "#888" }}>‰ªªÂä°Êï∞Ôºö{member.tasks}</div>
                </div>
                <select
                  value={member.role}
                  onChange={(e) => handleRoleChange(member.id, e.target.value)}
                >
                  <option value="Captain">Captain</option>
                  <option value="Member">Member</option>
                </select>
                {member.online && (
                  <span
                    style={{
                      marginLeft: "10px",
                      width: "10px",
                      height: "10px",
                      backgroundColor: "#22c55e",
                      borderRadius: "50%",
                      display: "inline-block",
                    }}
                  ></span>
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}
