// App.js
import React, { useState } from 'react';
import ReactMarkdown from 'react-markdown';
import './App.css';

function App() {
  const [city, setCity] = useState('');
  const [days, setDays] = useState(1);
  const [preference, setPreference] = useState('');
  const [plan, setPlan] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    const response = await fetch('http://localhost:3001/api/plan', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ city, days, preferences: preference })
    });
    const data = await response.json();
    setPlan(data.plan); // Markdown 格式
  };

  return (
    <div className="app-container">
      <h1>✈️ AI Travel Planner</h1>
      <form onSubmit={handleSubmit}>
        <input value={city} onChange={(e) => setCity(e.target.value)} placeholder="City" />
        <input value={days} onChange={(e) => setDays(e.target.value)} placeholder="Days" type="number" />
        <select value={preference} onChange={(e) => setPreference(e.target.value)}>
          <option value="">Select Preference</option>
          <option value="nature">Nature</option>
          <option value="history">History</option>
          <option value="food">Food</option>
        </select>
        <button type="submit">Generate Plan</button>
      </form>

      {plan && (
        <div className="plan-output">
          <ReactMarkdown>{plan}</ReactMarkdown>
        </div>
      )}
    </div>
  );
}

export default App;
