// src/pages/Home.js
import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import './Home.css';
import Navbar from './Navbar';
import Features from './Features';

const API_BASE = process.env.REACT_APP_API_BASE || 'http://localhost:3001';

function Home() {
  const navigate = useNavigate();
  const [checking, setChecking] = useState(true);
  const [loggedIn, setLoggedIn] = useState(false);

  // 1) 先看 URL 里是否带回了 ?userId=...
  useEffect(() => {
    const p = new URLSearchParams(window.location.search);
    const uid = p.get('userId');
    if (uid) {
      localStorage.setItem('userId', String(uid));
      setLoggedIn(true);
      // 清掉查询串，避免刷新后重复解析
      window.history.replaceState({}, '', window.location.pathname);
    }
  }, []);

  // 2) 若本地没有 userId，再尝试通过会话接口获取
  useEffect(() => {
    const ensureUserId = async () => {
      let id = localStorage.getItem('userId');
      if (id) {
        setLoggedIn(true);
        setChecking(false);
        return;
      }
      try {
        const r = await fetch(`${API_BASE}/api/auth/me`, { credentials: 'include' });
        const d = await r.json();
        if (d?.user?.id) {
          localStorage.setItem('userId', String(d.user.id));
          setLoggedIn(true);
          console.log('✅ 从 /api/auth/me 获取到 userId：', d.user.id);
        } else {
          console.warn('⚠️ 未登录（/api/auth/me 返回空）');
        }
      } catch (e) {
        console.warn('⚠️ 获取登录态失败：', e);
      } finally {
        setChecking(false);
      }
    };
    ensureUserId();
  }, []);

  return (
    <>
      <Navbar />
      <div className="home-container">
        <div className="main-content">
          <div className="left-panel">
            <div className="text-wrapper">
              <h1>Tired of chaotic travel planning?</h1>
              <h2><span className="highlight">Plan together.</span> Remember forever.</h2>

              <div className="button-row">
                <button
                  className="primary-btn"
                  onClick={() => navigate('/create')}
                  disabled={checking}
                >
                  {checking ? 'Checking…' : 'Start a New Plan'}
                </button>

                <button
                  className="secondary-btn"
                  onClick={() => navigate('/summary-card')}
                  disabled={checking}
                >
                  {checking ? 'Loading…' : 'View Existing Plans'}
                </button>
              </div>

              {!loggedIn && !checking && (
                <div style={{ marginTop: '12px' }}>
                  <button
                    className="secondary-btn"
                    onClick={() => {
                      // 触发后端发起 Google 登录
                      window.location.href = `${API_BASE}/api/auth/google`;
                    }}
                  >
                    Sign in with Google
                  </button>
                </div>
              )}

            </div>
          </div>
        </div>
      </div>
      <Features />
    </>
  );
}

export default Home;
