import React, { useState, useEffect } from "react";
import { useParams } from "react-router-dom";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./PackingList.css";

const defaultAvatar = "https://cdn-icons-png.flaticon.com/512/149/149071.png";

export default function MemberPage() {
  const { id } = useParams(); // 用户 ID
  const [tripData, setTripData] = useState([]); // ✅ 保存用户的所有行程
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [memberData, setMemberData] = useState([]);

  // 第一步：通过用户 ID 获取所有行程
  useEffect(() => {
    const fetchTrips = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
        const trips = await res.json();

        if (Array.isArray(trips) && trips.length > 0) {
          setTripData(trips);
          setSelectedTripId(trips[0].id); // 默认选第一个行程
        }
      } catch (err) {
        console.error("❌ 获取行程失败:", err);
      }
    };

    fetchTrips();
  }, [id]);

  // 第二步：当选中行程变化时，获取该行程的成员
  useEffect(() => {
    if (!selectedTripId) return;

    const fetchMembers = async () => {
      try {
        const res = await fetch(`http://localhost:3001/api/members?tripId=${selectedTripId}`);
        const members = await res.json();
        setMemberData(members);
      } catch (err) {
        console.error("❌ 获取成员失败:", err);
      }
    };

    fetchMembers();
  }, [selectedTripId]);

  // 修改角色并同步后端
  const handleRoleChange = async (memberId, newRole) => {
    try {
      await fetch(`http://localhost:3001/api/members/${memberId}`, {
        method: "PUT",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({ role: newRole }),
      });

      setMemberData((prev) =>
        prev.map((m) => (m.id === memberId ? { ...m, role: newRole } : m))
      );
    } catch (err) {
      console.error("❌ 更新角色失败:", err);
    }
  };

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="packing-page">
        <div className="trip-select">
          <label htmlFor="tripSelect">查看行程：</label>
          <select
            id="tripSelect"
            value={selectedTripId ?? ""}
            onChange={(e) => setSelectedTripId(Number(e.target.value))}
          >
            <option value="">请选择行程</option>
            {tripData.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} ➝ {trip.destination}
              </option>
            ))}
          </select>
        </div>

        <div className="packing-card">
          <div className="packing-header">
            <h3>👥 成员列表</h3>
          </div>

          <ul className="packing-items">
            {memberData.map((member) => (
              <li key={member.id} className="packing-item" style={{ display: "flex", alignItems: "center" }}>
                <img
                  src={defaultAvatar}
                  alt="avatar"
                  style={{ width: "36px", height: "36px", borderRadius: "50%", marginRight: "12px" }}
                />
                <div style={{ flex: 1 }}>
                  <strong>{member.name}</strong>
                  <div style={{ fontSize: "14px", color: "#888" }}>任务数：{member.tasks}</div>
                </div>
                <select
                  value={member.role}
                  onChange={(e) => handleRoleChange(member.id, e.target.value)}
                >
                  <option value="Captain">Captain</option>
                  <option value="Member">Member</option>
                </select>
                {member.online && (
                  <span
                    style={{
                      marginLeft: "10px",
                      width: "10px",
                      height: "10px",
                      backgroundColor: "#22c55e",
                      borderRadius: "50%",
                      display: "inline-block",
                    }}
                  ></span>
                )}
              </li>
            ))}
          </ul>
        </div>
      </div>
    </>
  );
}
