import React, { useState } from "react";

export default function AddAccommodationForm({ tripId, onSuccess }) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
  });
  const [imageFile, setImageFile] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!imageFile) return alert("请上传图片");

    const formData = new FormData();
    formData.append("tripId", tripId);
    formData.append("name", form.name);
    formData.append("address", form.address);
    formData.append("checkIn", form.checkIn);
    formData.append("checkOut", form.checkOut);
    formData.append("bookingUrl", form.bookingUrl);
    formData.append("image", imageFile); // ⬅️ 关键：图片文件

    try {
      const res = await fetch("http://localhost:3001/api/accommodations", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (data.id) {
        alert("✅ 添加成功");
        onSuccess?.();
        setForm({ name: "", address: "", checkIn: "", checkOut: "", bookingUrl: "" });
        setImageFile(null);
      } else {
        alert("❌ 添加失败");
      }
    } catch (err) {
      console.error("❌ 上传失败", err);
    }
  };

  return (
    <div className="add-accommodation-form">
      <h3>添加住宿</h3>
      <input name="name" value={form.name} onChange={handleChange} placeholder="住宿名称" />
      <input name="address" value={form.address} onChange={handleChange} placeholder="地址" />
      <input name="checkIn" type="date" value={form.checkIn} onChange={handleChange} />
      <input name="checkOut" type="date" value={form.checkOut} onChange={handleChange} />
      <input name="bookingUrl" value={form.bookingUrl} onChange={handleChange} placeholder="跳转链接" />
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleSubmit}>上传住宿信息</button>
    </div>
  );
}
