import React, { useState } from "react";

export default function AccommodationForm({ tripId }) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
  });
  const [imageFile, setImageFile] = useState(null);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    if (!form.name || !form.checkIn || !form.checkOut) return alert("ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØ");
    if (!imageFile) return alert("ËØ∑‰∏ä‰º†‰ΩèÂÆøÂõæÁâá");

    const formData = new FormData();
    formData.append("tripId", tripId);
    formData.append("name", form.name);
    formData.append("address", form.address);
    formData.append("checkIn", form.checkIn);
    formData.append("checkOut", form.checkOut);
    formData.append("bookingUrl", form.bookingUrl);
    formData.append("image", imageFile);

    try {
      const res = await fetch("http://localhost:3001/api/accommodations", {
        method: "POST",
        body: formData,
      });
      const data = await res.json();
      if (data.id) {
        alert("‚úÖ ‰∏ä‰º†ÊàêÂäü");
        setForm({ name: "", address: "", checkIn: "", checkOut: "", bookingUrl: "" });
        setImageFile(null);
      } else {
        alert("‚ùå ‰∏ä‰º†Â§±Ë¥•");
      }
    } catch (err) {
      console.error("‚ùå ‰∏ä‰º†Â§±Ë¥•", err);
    }
  };

  return (
    <div className="form-box">
      <input name="name" value={form.name} onChange={handleChange} placeholder="üè® ‰ΩèÂÆøÂêçÁß∞" />
      <input name="address" value={form.address} onChange={handleChange} placeholder="üìç Âú∞ÂùÄ" />
      <input name="checkIn" type="date" value={form.checkIn} onChange={handleChange} />
      <input name="checkOut" type="date" value={form.checkOut} onChange={handleChange} />
      <input name="bookingUrl" value={form.bookingUrl} onChange={handleChange} placeholder="üîó Ë∑≥ËΩ¨ÈìæÊé•ÔºàÂèØÈÄâÔºâ" />
      <input type="file" accept="image/*" onChange={handleFileChange} />
      <button onClick={handleSubmit}>‰∏ä‰º†</button>
    </div>
  );
}
