import React, { useRef, useState } from "react";
import "./AccommodationForm.css";

export default function AccommodationForm({
  userId,
  tripList,            // ‰ªÖÁî®‰∫éÂ§ñÂ±Ç‰∏ãÊãâÂ±ïÁ§∫ÔºàÊú¨ÁªÑ‰ª∂‰∏çÂº∫Âà∂Áî®Ôºâ
  selectedTripId,      // ÂøÖÈ°ªÔºöÂΩìÂâçÈÄâ‰∏≠ÁöÑ tripId
  setSelectedTripId,   // ÂèØÈÄâÔºöËã•‰Ω†ÊÉ≥Âú®Ë°®ÂçïÂÜÖÂàáÊç¢Ë°åÁ®ã
  onSuccess,           // ÊàêÂäüÂêéÂõûË∞ÉÔºàÂà∑Êñ∞ÂàóË°®Á≠âÔºâ
}) {
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [preview, setPreview] = useState("");
  const [loading, setLoading] = useState(false);
  const fileInputRef = useRef(null);

  // ÂèØË∞ÉÔºö‰∏ä‰º†Â§ßÂ∞èÈôêÂà∂ÔºàMBÔºâ
  const MAX_MB = 25;
  const ALLOW_TYPES = ["image/jpeg", "image/png", "image/webp"];

  const onChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const onPickFile = (e) => {
    const f = e.target.files?.[0];
    if (!f) return;
    if (!ALLOW_TYPES.includes(f.type)) return alert("‰ªÖÊîØÊåÅ JPG / PNG / WebP");
    if (f.size > MAX_MB * 1024 * 1024) return alert(`Êñá‰ª∂‰∏çÂæóË∂ÖËøá ${MAX_MB}MB`);
    setImageFile(f);
    setPreview(URL.createObjectURL(f));
  };

  // Áõ¥‰º† S3
  async function uploadToS3(file, { userId, tripId }) {
    // 1) Ë¶ÅÈ¢ÑÁ≠æÂêç
    const r1 = await fetch("http://localhost:3001/api/upload/sign", {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        contentType: file.type,
        userId,
        tripId,
        size: file.size, // ÂèØËÆ©ÂêéÁ´ØÂÅöÂ§ßÂ∞èÊ†°È™åÔºàÂèØÈÄâÔºâ
      }),
    });
    if (!r1.ok) throw new Error("Ëé∑ÂèñÁ≠æÂêçÂ§±Ë¥•Ôºö" + (await r1.text().catch(() => "")));
    const { uploadUrl, publicUrl, key } = await r1.json();

    // 2) PUT Âà∞ S3ÔºàContent-Type ÂøÖÈ°ª‰∏ÄËá¥Ôºâ
    const r2 = await fetch(uploadUrl, {
      method: "PUT",
      headers: { "Content-Type": file.type },
      body: file,
    });
    if (!r2.ok) throw new Error("‰∏ä‰º†Âà∞ S3 Â§±Ë¥•Ôºö" + (await r2.text().catch(() => "")));

    return { publicUrl, key };
  }

  async function handleSubmit(e) {
    e?.preventDefault?.();

    if (!selectedTripId) return alert("ËØ∑ÈÄâÊã©Ë°åÁ®ã");
    if (!form.name || !form.checkIn || !form.checkOut) {
      return alert("ËØ∑Â°´ÂÜôÂÆåÊï¥‰ø°ÊÅØÔºàÂêçÁß∞„ÄÅÂÖ•‰Ωè„ÄÅÈÄÄÊàøÔºâ");
    }

    try {
      setLoading(true);

      // ‚ë† ÂèØÈÄâÔºöÂÖàÁõ¥‰º† S3
      let imageUrl = "";
      let imageKey = "";
      if (imageFile) {
        const { publicUrl, key } = await uploadToS3(imageFile, {
          userId,
          tripId: selectedTripId,
        });
        imageUrl = publicUrl;
        imageKey = key;
      }

      // ‚ë° ‰øùÂ≠òÂà∞Êï∞ÊçÆÂ∫ì
      const res = await fetch("http://localhost:3001/api/accommodations", {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        body: JSON.stringify({
          tripId: selectedTripId,
          name: form.name,
          address: form.address,
          checkIn: form.checkIn,
          checkOut: form.checkOut,
          bookingUrl: form.bookingUrl,
          imageUrl,   // S3 Áõ¥Èìæ
          imageKey,   // S3 keyÔºàÂêéÁª≠Âà†Èô§/Êç¢ CDN Áî®Ôºâ
        }),
      });

      const data = await res.json().catch(() => ({}));
      if (!res.ok || !data?.id) throw new Error(data?.message || "‰øùÂ≠òÂ§±Ë¥•");

      alert("‚úÖ Ê∑ªÂä†ÊàêÂäü");
      // ÈáçÁΩÆË°®Âçï
      setForm({ name: "", address: "", checkIn: "", checkOut: "", bookingUrl: "" });
      setImageFile(null);
      setPreview("");
      if (fileInputRef.current) fileInputRef.current.value = "";
      onSuccess?.();
    } catch (err) {
      console.error(err);
      alert("‚ùå Â§±Ë¥•Ôºö" + (err.message || "Êú™Áü•ÈîôËØØ"));
    } finally {
      setLoading(false);
    }
  }

  return (
    <form className="form-card" onSubmit={handleSubmit}>
      <h3 className="accommodation-title">Add New Accommodation</h3>

      {/* ÈúÄË¶ÅÂú®Ë°®ÂçïÈáåÂàáÊç¢Ë°åÁ®ãÂÜçÊîæÂºÄ‰∏ãÈù¢ËøôÊÆµ */}
      {/* {tripList?.length > 0 && setSelectedTripId && (
        <select
          value={selectedTripId ?? ""}
          onChange={(e) => setSelectedTripId(Number(e.target.value))}
          style={{ marginBottom: 8 }}
        >
          {tripList.map((t) => (
            <option key={t.id} value={t.id}>
              {t.fromCity} ‚ûù {t.destination}
            </option>
          ))}
        </select>
      )} */}

      <input
        name="name"
        value={form.name}
        onChange={onChange}
        placeholder="üè® Accommodation Name"
      />
      <input
        name="address"
        value={form.address}
        onChange={onChange}
        placeholder="üìç Address"
      />
      <input
        name="checkIn"
        type="date"
        value={form.checkIn}
        onChange={onChange}
      />
      <input
        name="checkOut"
        type="date"
        value={form.checkOut}
        onChange={onChange}
      />
      <input
        name="bookingUrl"
        value={form.bookingUrl}
        onChange={onChange}
        placeholder="üîó Link"
      />

      <div style={{ display: "grid", gap: 6 }}>
        <input
          ref={fileInputRef}
          type="file"
          accept={ALLOW_TYPES.join(",")}
          onChange={onPickFile}
        />
        {preview && (
          <img
            src={preview}
            alt="preview"
            style={{ width: 240, height: 160, objectFit: "cover", borderRadius: 8 }}
          />
        )}
      </div>

      <button type="submit" disabled={loading}>
        {loading ? "‰∏ä‰º†‰∏≠..." : "Upload"}
      </button>
      <div style={{ fontSize: 12, color: "#6b7280", marginTop: 6 }}>
        ÊîØÊåÅÁ±ªÂûãÔºöJPG/PNG/WebPÔºåÊúÄÂ§ß {MAX_MB}MB
      </div>
    </form>
  );
}
