import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import Navbar from "./Navbar";
import "./SummaryCard.css";

export default function TravelSummaries() {
  const navigate = useNavigate();
  const [summary, setSummary] = useState(null);

  useEffect(() => {
  const stored = localStorage.getItem("myPlan");
  if (stored) {
    try {
      const parsed = JSON.parse(stored);
      const schedule = parsed.schedule || [];

      if (schedule.length > 0) {
        const startDate = schedule[0].date;
        const endDate = schedule[schedule.length - 1].date;
        const duration = `${schedule.length}Â§©${schedule.length - 1}Êôö`;

        // Áî®Á¨¨‰∏Ä‰∏™ÈùûÁ©∫Âú∞ÁÇπ‰Ωú‰∏∫ÁõÆÁöÑÂú∞ÊòæÁ§∫
        let destination = "Êú™ÊåáÂÆöÂüéÂ∏Ç";
        for (let day of schedule) {
          const firstItem = day.items?.[0];
          if (firstItem?.locationName || firstItem?.title) {
            destination = (firstItem.locationName || firstItem.title).split(",")[0]; // ÂèñÂüéÂ∏Ç
            break;
          }
        }

        const cardData = {
          city: destination,
          dateRange: `${startDate} Ëá≥ ${endDate}`,
          duration,
          placeCount: schedule.reduce(
            (acc, day) => acc + (day.items?.length || 0),
            0
          ),
          cover: "/images/default-cover.jpg"
        };

        setSummaries([cardData]); // üí°Êï∞ÁªÑÂåÖË£ÖÊàê‰∏Ä‰∏™ÂàóË°®
      }
    } catch (err) {
      console.error("Ëß£Êûê localStorage Â§±Ë¥•:", err);
    }
  }
}, []);


  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">üåê ÊàëÁöÑÊâÄÊúâË°åÁ®ã</h2>
        {summary ? (
          <div className="trip-card" onClick={() => navigate("/plan-summary")}>
            <div className="trip-left">
              <div className="trip-title">
                {summary.from}Ôºà{summary.startTime}Ôºâ‚Äî{summary.to}{summary.duration}Ê∏∏
              </div>
              <div className="trip-subinfo">
                {summary.duration}„Éª{summary.placeCount}‰∏™Âú∞ÁÇπ
              </div>
              <div className="trip-icons">
                <span className="icon-user">üë§</span>
                <span className="icon-plus">‚ûï</span>
              </div>
            </div>
            <div className="trip-right">
              <img
                src={summary.cover}
                alt="cover"
                className="trip-cover"
              />
            </div>
          </div>
        ) : (
          <p>ÊöÇÊó†Ë°åÁ®ã„ÄÇ</p>
        )}
      </div>
    </>
  );
}
