import React, { useState } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents } from 'react-leaflet';
import 'leaflet/dist/leaflet.css';
import L from 'leaflet';

// 修复默认图标不显示的问题
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

// 📍可选点组件
function LocationMarker({ onSelect }) {
  const [position, setPosition] = useState(null);

  useMapEvents({
    click(e) {
      const { lat, lng } = e.latlng;
      setPosition([lat, lng]);
      onSelect([lat, lng]); // 通知父组件
    },
  });

  // 示例：点击地图后用 OpenStreetMap API 获取地址
  const handleMapClick = async (e) => {
    const { lat, lng } = e.latlng;
    const response = await fetch(`https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lng}&format=json`);
    const data = await response.json();
    const locationName = data.display_name || "未知地点";
    handleLocationSelected(locationName);
  };

  return position ? <Marker position={position} /> : null;
}

export default function MapView({ onLocationSelected }) {
  return (
    <div style={{ height: '400px', width: '100%' }}>
      <MapContainer center={[30.25, 120.15]} zoom={10} scrollWheelZoom={true} style={{ height: '100%', width: '100%' }}>
        <TileLayer
          attribution='&copy; OpenStreetMap'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <LocationMarker onSelect={onLocationSelected} />
      </MapContainer>
    </div>
  );
}
