// TripPhotoPage.jsx
import React, { useEffect, useMemo, useState, useRef } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./TripPhoto.css";
import UploadPhotoForm from "./UploadPhotoForm";

// ★ 关键：用相对路径（浏览器看到的是同源请求）
const API_BASE = process.env.REACT_APP_API_BASE || "http://localhost:3001";

export default function TripPhotoPage() {
  // ---------- state ----------
  const [trips, setTrips] = useState([]);
  const [selectedTrip, setSelectedTrip] = useState(null);
  const [selectedDayIndex, setSelectedDayIndex] = useState(1);
  const [photos, setPhotos] = useState([]);
  const [previewPhoto, setPreviewPhoto] = useState(null);
  const [likes, setLikes] = useState({});
  const [comments, setComments] = useState([]);
  const [newComment, setNewComment] = useState("");
  const [visibilityFilter, setVisibilityFilter] = useState("public");
  const [urlMap, setUrlMap] = useState({}); // { [photoId]: presignedUrl }

  const retriedRef = useRef(new Set());

// ✅ 用 state 保存，并在首次加载时自动获取
const [userId, setUserId] = useState(null);

useEffect(() => {
  (async () => {
    // 1) URL 参数 ?userId=...
    const qs = new URLSearchParams(window.location.search);
    const fromUrl = qs.get('userId');
    if (fromUrl) {
      localStorage.setItem('userId', fromUrl);
      setUserId(Number(fromUrl));
      window.history.replaceState({}, '', window.location.pathname);
      return;
    }

    // 2) localStorage
    const cached = localStorage.getItem('userId');
    if (cached) {
      setUserId(Number(cached));
      return;
    }

    // 3) 会话接口
    try {
      const r = await fetch('/api/auth/me', { credentials: 'include' });
      if (r.ok) {
        const d = await r.json();
        if (d?.user?.id) {
          localStorage.setItem('userId', String(d.user.id));
          setUserId(Number(d.user.id));
        }
      }
    } catch {}
  })();
}, []);


  // ---------- derived ----------
  const days = useMemo(() => {
    if (!selectedTrip?.startDate || !selectedTrip?.endDate) return 0;
    const d =
      Math.ceil(
        (new Date(selectedTrip.endDate) - new Date(selectedTrip.startDate)) /
          (1000 * 60 * 60 * 24)
      ) + 1;
    return Math.max(d, 0);
  }, [selectedTrip?.startDate, selectedTrip?.endDate]);

  const filtered = useMemo(() => {
    const base = photos.filter((p) => p.dayIndex === selectedDayIndex);
    if (visibilityFilter === "all") return base;
    return base.filter((p) => p.visibility === visibilityFilter);
  }, [photos, selectedDayIndex, visibilityFilter]);

  const isOwner = selectedTrip?.userId === userId;

  // ---------- helpers ----------
  async function getViewUrl(p) {
    // public 直接用 imageUrl；private 需通过后端签发
    if (p.visibility === "public" && p.imageUrl) return p.imageUrl;
    if (!p.imageKey) return "";
    const r = await fetch(
      `/api/upload/view-url?key=${encodeURIComponent(p.imageKey)}&expiresIn=300`,
      { credentials: "include" } // 需要带会话
    );
    if (!r.ok) return "";
    const { url } = await r.json();
    return url || "";
  }

  async function retrySignAndSet(p) {
    if (!p?.id) return;
    if (retriedRef.current.has(p.id)) return;
    retriedRef.current.add(p.id);

    try {
      const fresh = await getViewUrl(p);
      if (!fresh) return;
      setUrlMap((prev) => ({ ...prev, [p.id]: fresh }));
    } catch (e) {
      console.warn("重新签名失败", e);
    }
  }

  // 批量签发当前需要显示的图
  useEffect(() => {
    let aborted = false;
    (async () => {
      const need = filtered.filter((p) => !urlMap[p.id]);
      if (!need.length) return;
      const entries = await Promise.all(
        need.map(async (p) => {
          try {
            const u = await getViewUrl(p);
            return [p.id, u];
          } catch {
            return [p.id, ""];
          }
        })
      );
      if (!aborted) {
        setUrlMap((prev) => {
          const next = { ...prev };
          for (const [id, u] of entries) next[id] = u;
          return next;
        });
      }
    })();
    return () => {
      aborted = true;
    };
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [filtered.map((p) => p.id).join(",")]);

  // ---------- fetchers ----------
  const fetchTrips = async () => {
    if (userId == null) return;
    try {
      const res = await fetch(`/api/trip/user/${userId}`, {
        credentials: "include",
      });
      const data = await res.json();
      setTrips(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ 获取旅行失败", err);
      alert("获取旅行列表失败");
    }
  };

  const fetchPhotos = async (tripId) => {
    if (!tripId || userId == null) return;
    try {
      const res = await fetch(`/api/photo/trip/${tripId}?userId=${userId}`, {
        credentials: "include",
      });
      const data = await res.json();
      setPhotos(Array.isArray(data) ? data : []);
      setUrlMap({});
      retriedRef.current = new Set();
    } catch (err) {
      console.error("❌ 获取照片失败", err);
      alert("获取照片失败");
      setPhotos([]);
      setUrlMap({});
      retriedRef.current = new Set();
    }
  };

  const fetchLikes = async (photoId) => {
    if (!photoId || userId == null) return;
    try {
      const res = await fetch(`/api/photo/${photoId}/likes?userId=${userId}`, {
        credentials: "include",
      });
      const data = await res.json();
      setLikes((prev) => ({ ...prev, [photoId]: data || {} }));
    } catch (err) {
      console.error("❌ 获取点赞信息失败", err);
    }
  };

  const fetchComments = async (photoId) => {
    if (!photoId) return;
    try {
      const res = await fetch(`/api/photo/${photoId}/comments`, {
        credentials: "include",
      });
      const data = await res.json();
      setComments(Array.isArray(data) ? data : []);
    } catch (err) {
      console.error("❌ 获取评论失败", err);
    }
  };

  // ---------- effects ----------
  useEffect(() => {
    fetchTrips();
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [userId]);

  useEffect(() => {
    if (!selectedTrip?.id) return;
    setVisibilityFilter("public");
    setSelectedDayIndex(1);
    fetchPhotos(selectedTrip.id);
    // eslint-disable-next-line react-hooks/exhaustive-deps
  }, [selectedTrip?.id]);

  // ---------- actions ----------
  const handleLike = async (photoId) => {
    if (!photoId || userId == null) return;
    try {
      await fetch(`/api/photo/${photoId}/like`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ userId }),
      });
      fetchLikes(photoId);
    } catch (err) {
      console.error("❌ 点赞失败", err);
    }
  };

  const handleAddComment = async () => {
    if (!previewPhoto?.id || !newComment.trim() || userId == null) return;
    try {
      await fetch(`/api/photo/${previewPhoto.id}/comments`, {
        method: "POST",
        headers: { "Content-Type": "application/json" },
        credentials: "include",
        body: JSON.stringify({ userId, content: newComment }),
      });
      setNewComment("");
      fetchComments(previewPhoto.id);
    } catch (err) {
      console.error("❌ 评论失败", err);
    }
  };

  const handlePreview = async (p) => {
    let url = urlMap[p.id];
    if (p.visibility !== "public") {
      try {
        url = await getViewUrl(p);
        setUrlMap((prev) => ({ ...prev, [p.id]: url }));
      } catch {
        url = "";
      }
    }
    setPreviewPhoto({ ...p, _viewUrl: url || p.imageUrl || "" });
    fetchLikes(p.id);
    fetchComments(p.id);
  };

  const handleDeletePhoto = async () => {
    if (!previewPhoto?.id) return;
    if (previewPhoto.uploadedBy !== userId) {
      alert("只能删除自己上传的照片");
      return;
    }
    const ok = window.confirm("确定删除这张照片？此操作不可恢复。");
    if (!ok) return;

    try {
      const res = await fetch(`/api/photo/${previewPhoto.id}?userId=${userId}`, {
        method: "DELETE",
        credentials: "include",
      });
      if (!res.ok && res.status !== 204) {
        throw new Error((await res.text().catch(() => "")) || "删除失败");
      }
      setPhotos((prev) => prev.filter((p) => p.id !== previewPhoto.id));
      setPreviewPhoto(null);
      setUrlMap((prev) => {
        const n = { ...prev };
        delete n[previewPhoto.id];
        return n;
      });
    } catch (e) {
      console.error("❌ 删除失败:", e);
      alert("删除失败：无权限或服务器错误");
    }
  };

  // ---------- render ----------
  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="trip-photo-page">
        <h2 className="page-title" style={{ fontWeight: "bold", fontSize: "30px", textAlign: "center" }}>
          My Travel Footprints
        </h2>

        <div className="trip-dropdown-wrapper" style={{ textAlign: "center", marginBottom: "1rem" }}>
          <div className="trip-dropdown">
            <label style={{ fontWeight: "bold", textAlign: "center" }}>Choose Trip:</label>
            <select
              onChange={(e) => {
                const tid = Number(e.target.value);
                const trip = trips.find((t) => t.id === tid);
                setSelectedTrip(trip || null);
              }}
            >
              <option value="">-- Select a Trip --</option>
              {trips.map((t) => (
                <option key={t.id} value={t.id}>
                  {t.fromCity} → {t.destination}
                </option>
              ))}
            </select>
          </div>
        </div>

        {selectedTrip && (
          <>
            <div className="filter-row" style={{ justifyContent: "center" }}>
              <div className="day-selector">
                <label style={{ fontWeight: "bold", textAlign: "center" }}>Select Day:</label>
                <select
                  value={selectedDayIndex}
                  onChange={(e) => setSelectedDayIndex(Number(e.target.value))}
                >
                  {Array.from({ length: days }, (_, i) => (
                    <option key={i} value={i + 1}>
                      Day {i + 1}
                    </option>
                  ))}
                </select>
              </div>

              <div className="visibility-filter">
                <label style={{ fontWeight: "bold" }}>Photo Visibility:</label>
                <select
                  value={visibilityFilter}
                  onChange={(e) => setVisibilityFilter(e.target.value)}
                >
                  <option value="public">Public</option>
                  <option value="private">Private</option>
                  <option value="all">All</option>
                </select>
              </div>
            </div>

            <div className="photo-card">
              <div className="photo-card-content">
                <div className="left-column">
                  <h3 className="day-title">🗓️ Day {selectedDayIndex}</h3>
                  <div className="photo-grid">
                    {filtered.map((p) => {
                      const src = urlMap[p.id] || p.imageUrl || "";
                      return (
                        <div
                          key={p.id}
                          className="photo-item"
                          onClick={() => handlePreview(p)}
                          title={p.visibility === "private" ? "Private photo" : "Public photo"}
                        >
                          {src ? (
                            <img
                              src={src}
                              alt="Uploaded"
                              loading="lazy"
                              onError={() => retrySignAndSet(p)}
                              key={`${p.id}-${src}`}
                            />
                          ) : (
                            <div style={{ width: "100%", paddingTop: "75%", background: "#eee" }} />
                          )}
                          <p>{p.placeName}</p>
                        </div>
                      );
                    })}
                  </div>
                </div>

                <div className="right-column">
                  <UploadPhotoForm
                    tripId={selectedTrip.id}
                    dayIndex={selectedDayIndex}
                    onUpload={() => fetchPhotos(selectedTrip.id)}
                    isOwner={isOwner}
                  />
                </div>
              </div>
            </div>
          </>
        )}

        {previewPhoto && (
          <div className="modal" onClick={() => setPreviewPhoto(null)}>
            <div className="modal-content" onClick={(e) => e.stopPropagation()}>
              <span className="close" onClick={() => setPreviewPhoto(null)}>
                &times;
              </span>

              {previewPhoto.uploadedBy === userId && (
                <button
                  onClick={handleDeletePhoto}
                  style={{ position: "absolute", right: 56, top: 10, padding: "6px 10px", borderRadius: 8, cursor: "pointer" }}
                  title="删除照片"
                >
                  Delete
                </button>
              )}

              <h4 style={{ fontSize: "2rem", fontWeight: "bold", textAlign: "center" }}>
                {previewPhoto.placeName}
              </h4>
              <p style={{ textAlign: "center" }}>{previewPhoto.description}</p>
              <img
                src={
                  previewPhoto._viewUrl ||
                  urlMap[previewPhoto.id] ||
                  previewPhoto.imageUrl ||
                  ""
                }
                alt="Large Preview"
                className="modal-image"
                onError={() => retrySignAndSet(previewPhoto)}
                key={`${previewPhoto.id}-${(previewPhoto._viewUrl || urlMap[previewPhoto.id] || previewPhoto.imageUrl || "")}`}
              />

              <div className="photo-interaction">
                <div className="like-section">
                  <button className="like-button" onClick={() => handleLike(previewPhoto.id)}>
                    💜 Like
                  </button>
                  <p className="like-count">👍 Like：{likes[previewPhoto.id]?.total || 0}</p>
                </div>

                <div className="comment-section">
                  <h4 style={{ fontWeight: "bold" }}>💬 Comment</h4>
                  <ul className="comment-list">
                    {comments.map((c) => (
                      <li key={c.id}>
                        <strong>{c.user?.email || "User"}</strong>: {c.content}
                      </li>
                    ))}
                  </ul>

                  <textarea
                    className="comment-input"
                    value={newComment}
                    onChange={(e) => setNewComment(e.target.value)}
                    placeholder="leave a comment..."
                  />
                  <button className="comment-submit" onClick={handleAddComment}>
                    Comment
                  </button>
                </div>
              </div>
            </div>
          </div>
        )}
      </div>
    </>
  );
}
