import React, { useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import "./SummaryCard.css";
import Navbar from "./Navbar";

export default function TravelSummaries() {
  const navigate = useNavigate();
  const [summaries, setSummaries] = useState([]);

  useEffect(() => {
    // Ëé∑ÂèñÊâÄÊúâËÆ°Âàí
    const storedPlans = localStorage.getItem("allPlans"); // Â≠òÁöÑÊòØÊï∞ÁªÑ
    if (storedPlans) {
      try {
        const parsed = JSON.parse(storedPlans);
        const mapped = parsed.map((plan) => {
          const { fromCity, destination, startDate, endDate, schedule } = plan;
          const totalDays = schedule?.length || 0;
          const totalItems = schedule?.reduce((sum, day) => sum + day.items.length, 0);

          return {
            fromCity,
            destination,
            startDate,
            endDate,
            totalDays,
            totalItems,
            cover: "/images/default-cover.jpg"
          };
        });
        setSummaries(mapped);
      } catch (err) {
        console.error("‚ùå Failed to parse allPlans:", err);
      }
    }
  }, []);

  return (
    <>
      <Navbar />
      <div className="summary-wrapper">
        <h2 className="summary-title">üåê My Travel Summaries</h2>

        {summaries.length === 0 ? (
          <p style={{ padding: "1rem" }}>No summaries found.</p>
        ) : (
          summaries.map((summary, index) => (
            <div
              className="summary-card"
              key={index}
              onClick={() => navigate(`/plan-summary/${index}`)}
            >
              <div className="summary-left">
                <div className="summary-route">
                  {summary.fromCity} ‚Üí {summary.destination}
                </div>
                <div className="summary-dates">
                  {summary.startDate} ~ {summary.endDate}
                </div>
                <div className="summary-count">
                  {summary.totalDays} Â§© ÔΩú ÂÖ± {summary.totalItems} ‰∏™Âú∞ÁÇπ
                </div>
              </div>
              <div className="summary-right">
                <img
                  src={summary.cover}
                  alt="cover"
                  className="summary-cover"
                />
              </div>
            </div>
          ))
        )}
      </div>
    </>
  );
}
