import React, { useState, useEffect } from "react";
import Navbar from "./Navbar";
import SubNavBar from "./SubNavBar";
import "./Accommodation.css";

export default function AddAccommodationPage() {
  const id = localStorage.getItem("userId");
  const [tripList, setTripList] = useState([]);
  const [selectedTripId, setSelectedTripId] = useState(null);
  const [form, setForm] = useState({
    name: "",
    address: "",
    checkIn: "",
    checkOut: "",
    bookingUrl: "",
  });
  const [imageFile, setImageFile] = useState(null);
  const [accommodations, setAccommodations] = useState([]);

  useEffect(() => {
    const fetchTrips = async () => {
      const res = await fetch(`http://localhost:3001/api/trip/user/${id}`);
      const data = await res.json();
      setTripList(data);
      if (data.length > 0) setSelectedTripId(data[0].id);
    };
    fetchTrips();
  }, [id]);

  useEffect(() => {
    const fetchAccommodations = async () => {
      if (!selectedTripId) return;
      const res = await fetch(`http://localhost:3001/api/accommodations/trip/${selectedTripId}`);
      const data = await res.json();
      data.sort((a, b) => new Date(a.checkIn) - new Date(b.checkIn));
      setAccommodations(data);
    };
    fetchAccommodations();
  }, [selectedTripId]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleFileChange = (e) => {
    setImageFile(e.target.files[0]);
  };

  const handleSubmit = async () => {
    const formData = new FormData();
    formData.append("tripId", selectedTripId);
    Object.keys(form).forEach((key) => formData.append(key, form[key]));
    formData.append("image", imageFile);

    const res = await fetch("http://localhost:3001/api/accommodations", {
      method: "POST",
      body: formData,
    });
    const data = await res.json();
    if (data.id) {
      alert("✅ 上传成功");
      setForm({ name: "", address: "", checkIn: "", checkOut: "", bookingUrl: "" });
      setImageFile(null);
      setAccommodations((prev) => [...prev, data].sort((a, b) => new Date(a.checkIn) - new Date(b.checkIn)));
    } else {
      alert("❌ 上传失败");
    }
  };

  return (
    <>
      <Navbar />
      <SubNavBar />
      <div className="accommodation-container">
        <div className="form-section">
          <h2>🏠 添加住宿信息</h2>
          <select value={selectedTripId || ""} onChange={(e) => setSelectedTripId(Number(e.target.value))}>
            {tripList.map((trip) => (
              <option key={trip.id} value={trip.id}>
                {trip.fromCity} ➝ {trip.destination}
              </option>
            ))}
          </select>
          <input name="name" value={form.name} onChange={handleChange} placeholder="🏨 住宿名称" />
          <input name="address" value={form.address} onChange={handleChange} placeholder="📍 地址" />
          <input name="checkIn" type="date" value={form.checkIn} onChange={handleChange} />
          <input name="checkOut" type="date" value={form.checkOut} onChange={handleChange} />
          <input name="bookingUrl" value={form.bookingUrl} onChange={handleChange} placeholder="🔗 跳转链接（可选）" />
          <input type="file" accept="image/*" onChange={handleFileChange} />
          <button onClick={handleSubmit}>上传</button>
        </div>

        <div className="timeline-section">
          {accommodations.map((a) => (
            <div key={a.id} className="accommodation-card">
              <img src={a.imageUrl} alt="住宿图片" className="accommodation-image" />
              <div className="accommodation-info">
                <h4>{a.name}</h4>
                <p>{a.address}</p>
                <p>📅 {new Date(a.checkIn).toLocaleDateString()} - {new Date(a.checkOut).toLocaleDateString()}</p>
                {a.bookingUrl && (
                  <a href={a.bookingUrl} target="_blank" rel="noreferrer">🔗 查看预订页面</a>
                )}
              </div>
            </div>
          ))}
        </div>
      </div>
    </>
  );
}
