import React, { useState, useMemo } from "react";

const ALLOW_TYPES = ["image/jpeg", "image/png", "image/webp"];
const MAX_MB = 15;

// Âª∫ËÆÆÂ§çÁî®‰Ω†ÁöÑ API_BASE
const API_BASE = import.meta.env?.VITE_API_BASE || "http://localhost:3001";

export default function UploadPhotoForm({ tripId, dayIndex, onUpload }) {
  const [files, setFiles] = useState([]);
  const [placeName, setPlaceName] = useState("");
  const [description, setDescription] = useState("");
  const [visibility, setVisibility] = useState("public"); // Âè™Áî®‰∫éËêΩÂ∫ìÊùÉÈôêÔºå‰∏çÂΩ±ÂìçÊ°∂
  const [uploading, setUploading] = useState(false);

  const userId = useMemo(() => Number(localStorage.getItem("userId")), []);

  const handlePick = (e) => {
    const picked = Array.from(e.target.files || []);
    const bad = [];
    const ok = picked.filter((f) => {
      const validType = ALLOW_TYPES.includes(f.type);
      const validSize = f.size <= MAX_MB * 1024 * 1024;
      if (!validType || !validSize) bad.push(f.name);
      return validType && validSize;
    });
    if (bad.length) {
      alert(`‰ª•‰∏ãÊñá‰ª∂‰∏çÁ¨¶ÂêàË¶ÅÊ±ÇÔºàJPG/PNG/WebPÔºå‚â§ ${MAX_MB}MBÔºâÔºö\n` + bad.join("\n"));
    }
    setFiles(ok);
  };

  // üëâ Âè™‰º† kind:"photo"ÔºåËÆ©ÂêéÁ´ØÁªü‰∏ÄÊîæÂà∞‚ÄúÁÖßÁâáÁßÅÊúâÊ°∂‚Äù
  async function getSignedUrl(file, { userId, tripId }) {
    const r = await fetch(`${API_BASE}/api/upload/sign`, {
      method: "POST",
      headers: { "Content-Type": "application/json" },
      body: JSON.stringify({
        contentType: file.type,
        userId,
        tripId,
        kind: "photo",     // ‚úÖ Âõ∫ÂÆö‰ΩøÁî® photoÔºõ‰∏çÂÜç‰º† visibility
        size: file.size,
      }),
    });
    if (!r.ok) throw new Error("Ëé∑ÂèñÁ≠æÂêçÂ§±Ë¥•Ôºö" + (await r.text().catch(() => "")));
    return r.json(); // { uploadUrl, key, bucket }
  }

  const handleUpload = async () => {
    if (!userId || !tripId || dayIndex == null) {
      alert("Áº∫Â∞ëÂøÖË¶Å‰ø°ÊÅØÔºàuserId / tripId / dayIndexÔºâ");
      return;
    }
    if (!files.length) return alert("ËØ∑ÈÄâÊã©ÁÖßÁâá");

    setUploading(true);
    try {
      for (const file of files) {
        // 1) Âèñ PUT È¢ÑÁ≠æÂêçÔºàËøõÂÖ•ÁßÅÊúâÊ°∂Ôºâ
        const { uploadUrl, key } = await getSignedUrl(file, { userId, tripId });

        // 2) Áõ¥‰º† S3ÔºàContent-Type ÂøÖÈ°ª‰∏ÄËá¥Ôºâ
        const putRes = await fetch(uploadUrl, {
          method: "PUT",
          headers: { "Content-Type": file.type },
          body: file,
        });
        if (!putRes.ok) {
          throw new Error(`‰∏ä‰º†Âà∞ S3 Â§±Ë¥•Ôºö${file.name} ‚Äî ` + (await putRes.text().catch(() => "")));
        }

        // 3) ËêΩÂ∫ì ‚Äî‚Äî ËøôÈáåÊâçÂå∫ÂàÜ public/private
        //    Âõ†‰∏∫ÈÉΩÂú®ÁßÅÊúâÊ°∂ÈáåÔºåÊâÄ‰ª• imageUrl ‰∏ÄÂæã nullÔºàÂ±ïÁ§∫Êó∂Áî® GET È¢ÑÁ≠æÂêçÔºâ
        const metaRes = await fetch(`${API_BASE}/api/photo`, {
          method: "POST",
          headers: { "Content-Type": "application/json" },
          body: JSON.stringify({
            tripId: Number(tripId),
            uploadedBy: Number(userId),
            dayIndex: Number(dayIndex),
            placeName: placeName || null,
            description: description || null,
            visibility,          // ‚úÖ ÊùÉÈôêÊéßÂà∂Âú®Â∫ìÈáå
            imageKey: key,       // ‚úÖ ÂøÖÈ°ª
            imageUrl: null,      // ‚úÖ Áªü‰∏ÄÁßÅÊúâÊ°∂Ôºå‰∏çÂÜôÁõ¥Èìæ
          }),
        });
        if (!metaRes.ok) {
          throw new Error(`‰øùÂ≠òÂÖÉÊï∞ÊçÆÂ§±Ë¥•Ôºö${file.name} ‚Äî ` + (await metaRes.text().catch(() => "")));
        }
      }

      setFiles([]);
      setPlaceName("");
      setDescription("");
      alert("‚úÖ ÂÖ®ÈÉ®ÁÖßÁâá‰∏ä‰º†ÊàêÂäü");
      onUpload?.();
    } catch (err) {
      console.error("‚ùå ‰∏ä‰º†Â§±Ë¥•:", err);
      alert(err.message || "‰∏ä‰º†Â§±Ë¥•");
    } finally {
      setUploading(false);
    }
  };

  return (
    <div className="upload-form-card">
      <h4>üì§ Upload New Photo</h4>

      <input type="file" multiple accept={ALLOW_TYPES.join(",")} onChange={handlePick} />

      <input
        type="text"
        placeholder="üìç Place Name"
        value={placeName}
        onChange={(e) => setPlaceName(e.target.value)}
      />

      <input
        type="text"
        placeholder="üìù Description"
        value={description}
        onChange={(e) => setDescription(e.target.value)}
      />

      {/* ‰ªÖÂΩ±ÂìçËêΩÂ∫ìÂêéÁöÑËÆøÈóÆÊùÉÈôêÔºå‰∏çÂΩ±Âìç S3 Ê°∂ */}
      <select value={visibility} onChange={(e) => setVisibility(e.target.value)}>
        <option value="public">üåç Public (ÂØπÊóÖ‰º¥ÂèØËßÅ)</option>
        <option value="private">üîí Private (‰ªÖ‰∏ä‰º†ËÄÖ)</option>
      </select>

      <button onClick={handleUpload} disabled={!files.length || uploading}>
        {uploading ? "Uploading..." : "Upload"}
      </button>

      {!!files.length && (
        <div style={{ marginTop: 8, fontSize: 12, color: "#6b7280" }}>
          Â∑≤ÈÄâ {files.length} Âº†Ôºõ‰ªÖÊîØÊåÅ JPG/PNG/WebPÔºåÂçïÂº† ‚â§ {MAX_MB}MB
        </div>
      )}
    </div>
  );
}
