import React, { useState, useEffect } from 'react';
import { MapContainer, TileLayer, Marker, useMapEvents, useMap } from 'react-leaflet';
import L from 'leaflet';
import 'leaflet/dist/leaflet.css';
import 'leaflet-control-geocoder/dist/Control.Geocoder.css';
import 'leaflet-control-geocoder';

// ä¿®å¤é»˜è®¤å›¾æ ‡ä¸æ˜¾ç¤º
delete L.Icon.Default.prototype._getIconUrl;
L.Icon.Default.mergeOptions({
  iconUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon.png',
  iconRetinaUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-icon-2x.png',
  shadowUrl: 'https://unpkg.com/leaflet@1.9.4/dist/images/marker-shadow.png',
});

// ðŸ“æ·»åŠ  Geocoder æŽ§ä»¶
function GeocoderControl() {
  const map = useMap();

  useEffect(() => {
    if (!map) return;

    const geocoder = L.Control.geocoder({
      defaultMarkGeocode: true,
      placeholder: 'Search location...',
      geocoder: L.Control.Geocoder.nominatim(),
    }).addTo(map);

    return () => map.removeControl(geocoder);
  }, [map]);

  return null;
}

// ðŸ“åœ°å›¾ç‚¹å‡»é€‰ç‚¹
function LocationMarker({ onSelect }) {
  const [position, setPosition] = useState(null);

  useMapEvents({
    click: async (e) => {
      const { lat, lng } = e.latlng;
      setPosition([lat, lng]);

      try {
        const response = await fetch(
          `https://nominatim.openstreetmap.org/reverse?lat=${lat}&lon=${lng}&format=json&accept-language=en`
        );
        const data = await response.json();
        const locationName = data.display_name || "Unknown Location";
        onSelect(locationName);
      } catch (error) {
        console.error("Failed to get location:", error);
        onSelect("Unknown Location");
      }
    },
  });

  return position ? <Marker position={position} /> : null;
}

export default function MapView({ onLocationSelected }) {
  return (
    <div style={{ height: '400px', width: '100%' }}>
      <MapContainer center={[30.25, 120.15]} zoom={10} scrollWheelZoom={true} style={{ height: '100%', width: '100%' }}>
        <TileLayer
          attribution='&copy; OpenStreetMap'
          url="https://{s}.tile.openstreetmap.org/{z}/{x}/{y}.png"
        />
        <GeocoderControl />
        <LocationMarker onSelect={onLocationSelected} />
      </MapContainer>
    </div>
  );
}
